  1563→function getProfanitySettings() {
  1564→  try {
  1565→    const saved = localStorage.getItem(PROFANITY_SETTINGS_KEY);
  1566→    if (saved) {
  1567→      const parsed = JSON.parse(saved);
  1568→      return {
  1569→        ...DEFAULT_PROFANITY_SETTINGS,
  1570→        ...parsed
  1571→      };
  1572→    }
  1573→  } catch (e) {
