   390→
   391→/**
   392→ * SECURITY: Escape HTML to prevent XSS attacks
   393→ * Use this when inserting untrusted content into innerHTML
   394→ * @param {string} str - String to escape
   395→ * @returns {string} Escaped string safe for innerHTML
   396→ */
   397→function escapeHtml(str) {
   398→    if (str === null || str === undefined) return '';
   399→    if (typeof str !== 'string') str = String(str);
   400→
   401→    const escapeMap = {
   402→        '&': '&amp;',
   403→        '<': '&lt;',
   404→        '>': '&gt;',
   405→        '"': '&quot;',
   406→        "'": '&#x27;',
   407→        '/': '&#x2F;',
   408→        '`': '&#x60;'
   409→    };
   410→
   411→    return str.replace(/[&<>"'\/`]/g, char => escapeMap[char]);
   412→}
   413→
   414→/**
   415→ * SECURITY: Create safe HTML from template with escaped values
   416→ * @param {TemplateStringsArray} strings - Template strings
   417→ * @param {...any} values - Values to escape
   418→ * @returns {string} HTML with escaped values
   419→ */
   420→function safeHtml(strings, ...values) {
   421→    return strings.reduce((result, str, i) => {
   422→        const value = values[i - 1];
   423→        const escaped = escapeHtml(value);
   424→        return result + escaped + str;
   425→    });
   426→}
   427→
   428→/**
   429→ * Format time in MM:SS or HH:MM:SS
   430→ */
   431→function formatTime(seconds) {
   432→    if (isNaN(seconds) || seconds < 0) return '0:00';
   433→
   434→    const hours = Math.floor(seconds / 3600);
   435→    const minutes = Math.floor((seconds % 3600) / 60);
   436→    const secs = Math.floor(seconds % 60);
   437→
   438→    if (hours > 0) {
   439→        return `${hours}:${minutes.toString().padStart(2, '0')}:${secs.toString().padStart(2, '0')}`;
   440→    }
   441→    return `${minutes}:${secs.toString().padStart(2, '0')}`;
   442→}
   443→
   444→/**
   445→ * Set status message
   446→ */
   447→function setStatus(message, isError = false) {
   448→    const status = document.getElementById('status');
   449→    if (status) {
   450→        status.textContent = message;
   451→        status.style.color = isError ? '#dc3545' : '#888';
   452→    }
   453→}
   454→
   455→/**
   456→ * Debounce function calls
   457→ */
   458→function debounce(func, wait) {
   459→    let timeout;
   460→    return function executedFunction(...args) {
   461→        const later = () => {
   462→            clearTimeout(timeout);
   463→            func(...args);
   464→        };
   465→        clearTimeout(timeout);
   466→        timeout = setTimeout(later, wait);
   467→    };
   468→}
