   715→/**
   716→ * Validate license key format: SPLICE-XXXX-XXXX-XXXX
   717→ * Character set excludes confusing chars: 0/O, 1/I/L
   718→ */
   719→function isValidLicenseKeyFormat(key) {
   720→    if (!key || typeof key !== 'string') return false;
   721→    const normalized = key.toUpperCase().trim();
   722→    const pattern = /^SPLICE-[ABCDEFGHJKMNPQRSTUVWXYZ23456789]{4}-[ABCDEFGHJKMNPQRSTUVWXYZ23456789]{4}-[ABCDEFGHJKMNPQRSTUVWXYZ23456789]{4}$/;
   723→    return pattern.test(normalized);
   724→}
