   813→                console.log('[SPLICE DEBUG] Error stack:', err?.stack);
   814→                console.log('[SPLICE DEBUG] Backend URL:', getBackendUrl());
   815→                console.log('[SPLICE DEBUG] Fetch timeout:', SPLICE_CONFIG.FETCH_TIMEOUT);
   816→                console.log('[SPLICE DEBUG] Is CEP environment:', typeof CSInterface !== 'undefined');
   817→                console.log('[SPLICE DEBUG] User agent:', navigator?.userAgent);
   818→                console.log('[SPLICE DEBUG] Error constructor:', err?.constructor?.name);
   819→                console.log('[SPLICE DEBUG] Is TypeError:', err?.name === 'TypeError');
   820→                console.log('[SPLICE DEBUG] Message contains "Failed to fetch":', err?.message?.includes('Failed to fetch'));
   821→                console.log('[SPLICE DEBUG] Message contains "fetch":', err?.message?.toLowerCase()?.includes('fetch'));
   822→                console.log('[SPLICE DEBUG] Message contains "network":', err?.message?.toLowerCase()?.includes('network'));
   823→                console.log('[SPLICE DEBUG] Message contains "CORS":', err?.message?.toLowerCase()?.includes('cors'));
   824→                console.log('[SPLICE DEBUG] Message contains "SSL":', err?.message?.toLowerCase()?.includes('ssl'));
   825→                console.log('[SPLICE DEBUG] Message contains "certificate":', err?.message?.toLowerCase()?.includes('certificate'));
   826→                console.log('[SPLICE DEBUG] Full error object:', JSON.stringify(err, Object.getOwnPropertyNames(err)));
   827→                console.log('[SPLICE DEBUG] ================================================');
   828→
   829→                // Provide detailed error message based on error type
   830→                let errorMessage = 'Connection error. ';
   831→
   832→                if (err.message === 'Request timed out') {
