        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ key: licenseKey })
      }, FETCH_TIMEOUT_DEFAULT);

      const result = await response.json();

      if (!result.success) {
        showLoginError(result.error || 'Activation failed');
        return;
      }

      // Save customer ID from activation response
      saveSettings({ customerId: result.customerId });

      // Hide modal
      loginModal?.classList.add('hidden');

      // Refresh credits display
      if (typeof refreshCredits === 'function') {
        await refreshCredits();
      }

      if (typeof setStatus === 'function') {
        setStatus(`License activated! ${result.tierName} tier - ${result.hoursRemaining.toFixed(1)}h remaining`);
      }
    } catch (err) {
      console.error('[SPLICE] License activation error:', err);

      // DIAGNOSTIC LOGGING: Capture exact error details for debugging
      console.log('[SPLICE DEBUG] === LICENSE ACTIVATION ERROR DIAGNOSTICS ===');
      console.log('[SPLICE DEBUG] Error name:', err?.name);
      console.log('[SPLICE DEBUG] Error message:', err?.message);
      console.log('[SPLICE DEBUG] Error stack:', err?.stack);
      console.log('[SPLICE DEBUG] Backend URL:', getBackendUrl());
      console.log('[SPLICE DEBUG] Fetch timeout:', FETCH_TIMEOUT_DEFAULT);
      console.log('[SPLICE DEBUG] Is UXP environment:', typeof uxp !== 'undefined');
      console.log('[SPLICE DEBUG] User agent:', navigator?.userAgent);
      console.log('[SPLICE DEBUG] ===============================================');

      // Provide detailed error message based on error type
      let errorMessage = 'Connection error. ';

      if (err.message === 'Request timed out') {
        errorMessage += 'Server is not responding. Check if backend is running.';
      } else if (err.message?.includes('Failed to fetch') || err.name === 'TypeError') {
        errorMessage += `Cannot reach ${getBackendUrl()}. Check network/firewall.`;
      } else if (err.message?.includes('CORS')) {
        errorMessage += 'CORS error. Contact support.';
      } else if (err.message?.includes('SSL') || err.message?.includes('certificate')) {
        errorMessage += 'SSL certificate error. Try updating your system certificates.';
      } else if (err.message) {
        errorMessage += err.message;
      } else {
        errorMessage += 'Unknown error. Check console for details.';
      }

      showLoginError(errorMessage);
    } finally {
      saveLoginBtn.disabled = false;
      saveLoginBtn.textContent = 'Activate';
    }
  });
}

