const FETCH_TIMEOUT_DEFAULT = 30000;      // 30 seconds for most requests
const FETCH_TIMEOUT_HEALTH = 5000;        // 5 seconds for health checks
const FETCH_TIMEOUT_PROCESSING = 120000;  // 2 minutes for processing endpoints

/**
 * Fetch with timeout support (AbortController-based).
 * Prevents UI from freezing if server hangs.
 * @param {string} url - URL to fetch
 * @param {Object} options - Fetch options
 * @param {number} [timeout] - Timeout in milliseconds (default: FETCH_TIMEOUT_DEFAULT)
 * @returns {Promise<Response>} Fetch response
 * @throws {Error} 'Request timed out' if timeout exceeded
 */
async function fetchWithTimeout(url, options = {}, timeout = FETCH_TIMEOUT_DEFAULT) {
  const controller = new AbortController();
  const timeoutId = setTimeout(() => controller.abort(), timeout);

  try {
    const response = await fetch(url, {
      ...options,
      signal: controller.signal
    });
    clearTimeout(timeoutId);
    return response;
  } catch (err) {
    clearTimeout(timeoutId);
    if (err.name === 'AbortError') {
      throw new Error('Request timed out');
    }
    throw err;
  }
}

/**
 * Parse error response from API with consistent fallback.
 * Handles various error response formats gracefully.
 * @param {Response} response - Fetch response object
 * @returns {Promise<string>} Error message string
 */
async function parseErrorResponse(response) {
  try {
    const data = await response.json();
    return data.error || data.message || `Error ${response.status}`;
  } catch {
    return `Error ${response.status}: ${response.statusText || 'Unknown error'}`;
  }
}

// ============================================================================
// OFFLINE DETECTION
// ============================================================================

// Track online/offline state
let _isOnline = typeof navigator !== 'undefined' ? navigator.onLine : true;
let _offlineCallbacks = [];

/**
 * Check if the browser/app is online
 * @returns {boolean} True if online
 */
function isOnline() {
  // navigator.onLine can be undefined in some UXP contexts
  if (typeof navigator !== 'undefined' && typeof navigator.onLine === 'boolean') {
    return navigator.onLine;
  }
  return _isOnline;
}

/**
 * Register a callback for online/offline state changes
 * @param {Function} callback - Called with (isOnline: boolean) on state change
 * @returns {Function} Unsubscribe function
 */
function onOnlineStateChange(callback) {
  _offlineCallbacks.push(callback);
  return () => {
    _offlineCallbacks = _offlineCallbacks.filter(cb => cb !== callback);
  };
}

/**
 * Update the UI to reflect online/offline status
 * @param {boolean} online - Current online state
 */
function updateOfflineUI(online) {
  const statusEl = document.getElementById('status');
  const goBtn = document.getElementById('goBtn');
  const offlineIndicator = document.getElementById('offlineIndicator');

  if (!online) {
    // Show offline state
    if (statusEl && !statusEl.dataset.originalText) {
      statusEl.dataset.originalText = statusEl.textContent;
    }
    if (statusEl) statusEl.textContent = '⚠ Offline - Check your connection';

    if (goBtn) {
      goBtn.disabled = true;
      goBtn.title = 'Cannot process while offline';
    }

