async function remove_silences(showMessageFlag = true, mode = "basic", silences = null, method = "jsx_and_compare", unit = "Seconds", operation = "delete", singleTrackMode = false, targetTrackIndex = null, targetTrackType = null, ripple = true, anchorEvery = 10, showProgress = false, j_cut = false, j_cut_seconds = 0, hideSpinnerAtTheEnd = true) {
    console.log("silences", silences);
    const licenseVerified = await verify_license_throttled();
    if (!licenseVerified) {
        showError("Please provide a valid license key in the Settings menu");
        return;
    }
    if (!check_function_availability("remove_silences")) {
        showError("Please provide a valid license key in the Settings menu");
        return;
    }
    test_52_schedule_log_upload(time_delay = 0);
    regular_license_key_checker();
    showSpinner();
    const trackerId = tracker.start("remove_silences");
    try {
        if (method == "sequential") {
            await remove_silences_sequential(showMessageFlag, "advanced", silences, "sequential", "Frames", operation, singleTrackMode, targetTrackIndex, targetTrackType, ripple, anchorEvery, showProgress);
            return;
        }
        if (method == "turbo") {
            console.log("CHECKING FOR JCUT FLAG");
            if (j_cut) {
                console.log("J-CUT FLAG DETECTED");
                var trackNames = await buildTrackNamesArray();
                var audioTrackNames = trackNames[1];
                isAudioTrackEmpty = [];
                for (var i = 0; i < audioTrackNames.length; i++) {
                    var evalString = "getNumClipsInTrack(" + i + ', "audio")';
                    console.log("eval_string", evalString);
                    var numClips = await new Promise((resolve, reject) => {
                        jsx.evalScript(evalString, function(result) {
                            console.log("result", result);
                            resolve(parseInt(result));
                        });
                    });
                    numClips == 0 ? isAudioTrackEmpty.push(true) : isAudioTrackEmpty.push(false);
                }
                console.log("J-CUT FLAG DETECTED");
                for (let i = 0; i < audioTrackNames.length; i++) {
                    console.log("j", i);
                    if (isAudioTrackEmpty[i]) {
                        console.log("audio track is empty, skipping");
                        continue;
                    }
                    var evalString = "moveAllClipsInTrack(" + i + ', "audio", ' + j_cut_seconds + ")";
                    console.log("eval_string", evalString);
                    await new Promise((resolve, reject) => {
                        jsx.evalScript(evalString, function(result) {
                            console.log("result", result);
                            resolve(result);
                        });
                    });
                }
                silences = silences.map(range => [range[0] + j_cut_seconds, range[1] + j_cut_seconds]);
            }
            const turboParams = await test_57_get_turbo_cutting_params();
            if (turboParams.useLegacy) {
                await xml_8(silences, turboParams);
                await delay(3000);
            } else {
                const frameRate = await jsxProject.getFrameRate();
                const timebase = await jsxProject.getTimebase();
                const frameDuration = timebase * frameRate;
                await turboSilenceCutting(silences.map(range => {
                    var start = Math.trunc(range[0] * frameDuration);
                    var end = Math.trunc(range[1] * frameDuration);
                    return {
                        from: start,
                        to: end,
                        diff: end - start
                    };
                }));
            }
            if (j_cut) {
                for (let i = 0; i < audioTrackNames.length; i++) {
                    if (isAudioTrackEmpty[i]) continue;
                    var evalString = "moveAllClipsInTrack(" + i + ', "audio", ' + -1 * j_cut_seconds + ")";
                    console.log("eval_string", evalString);
                    await new Promise((resolve, reject) => {
                        jsx.evalScript(evalString, function(result) {
                            console.log("result", result);
                            resolve(result);
                        });
                    });
                }
            }
            return;
        }
        keyAudioTrackIndex = 0;
        try {
            if (mode == "basic") {
                keyAudioTrackIndex = parseInt($("#silence-cutting-audio-track-basic").val());
            }
            if (mode == "advanced") {
                keyAudioTrackIndex = parseInt($("#silence-cutting-audio-track-advanced").val());
            }
        } catch (err) {
            console.log("error while reading master audio track", err);
        }
        if (isNaN(keyAudioTrackIndex)) {
            keyAudioTrackIndex = 0;
        }
        if (mode == "basic") {
            showSpinner(spinner_msg_muting);
            var evalString = "getMuteState()";
            console.log("eval_string", evalString);
            const muteState = await new Promise((resolve, reject) => {
                jsx.evalScript(evalString, function(result) {
                    console.log("result", result);
                    resolve(JSON.parse(result));
                });
            });
            var modifiedMuteState = JSON.parse(JSON.stringify(muteState));
            modifiedMuteState.audio = modifiedMuteState.audio.map((isMuted, index) => +(index !== keyAudioTrackIndex));
            evalString = "setMuteState('" + JSON.stringify(modifiedMuteState) + "')";
            console.log("eval_string", evalString);
            await new Promise((resolve, reject) => {
                jsx.evalScript(evalString, function(result) {
                    console.log("result of setting mute state", result);
                    resolve(result);
                });
            });
        }
        evalString = "app.project.activeSequence.getWorkAreaInPoint()";
        console.log("eval_string", evalString);
        const workAreaIn = await new Promise((resolve, reject) => {
            jsx.evalScript(evalString, function(result) {
                console.log("result", result);
                resolve(parseFloat(result));
            });
        });
        evalString = "app.project.activeSequence.getWorkAreaOutPoint()";
        console.log("eval_string", evalString);
        var workAreaOut = await new Promise((resolve, reject) => {
            jsx.evalScript(evalString, function(result) {
                console.log("result", result);
                resolve(parseFloat(result));
            });
        });
        var duration = workAreaOut - workAreaIn;
        if (mode == "basic") {
            console.log("BASIC MODE. Detecting silences");
            showSpinner(spinner_msg_rendering);
            file_path = await test_1_extract_audio_from_sequence();
            console.log("test_1_extract_audio_from_sequence complete");
            silences = await test_2_detect_silences(file_path);
        } else {
            console.log("ADVANCED MODE. Using provided silences");

