audioChannelMapping.audioClipsNumber = 0;
item.setAudioChannelMapping(audioChannelMapping);
}
if ((clip_width == null) || (clip_height == null)) { 
console.log("Getting dimensions from Premiere Pro");
try {
const dimensions = getProjectItemDimensions(item);
console.log("Dimensions:", JSON.stringify(dimensions));
if (dimensions) { 
clip_width = dimensions.width;
clip_height = dimensions.height;
}
} catch (error) {console.log("Error getting project item dimensions:", error);
