}}
if (clip) { 
var sequence_height = parseInt(app.project.activeSequence.getSettings().videoFrameHeight);
var sequence_width = parseInt(app.project.activeSequence.getSettings().videoFrameWidth);
var scale_required = 100 * Math.max(sequence_height / clip_height, sequence_width / clip_width);
if (scaling_mode === "fit") { 
scale_required = 100 * Math.min(sequence_height / clip_height, sequence_width / clip_width);
}
console.log("SET SCALE", sequence_height, sequence_width, clip_height, clip_width, scale_required, scaling_mode);

