 * Links audio clips to video clips in the specified tracks using JSX.
 * @param {number} videoTrackIndex - Index of the video track.
 * @param {number} audioTrackIndex - Index of the audio track.
 * @returns {Promise<any>} Result from the JSX script.
 */
async function linkAudioClipsToVideoClips(videoTrackIndex, audioTrackIndex) {
    const evalString = 'linkIdenticalClipsInTracks(' + videoTrackIndex + ', ' + audioTrackIndex + ')';
    console.log(evalString);
    const result = await new Promise((resolve, reject) => {
        jsx.evalScript(evalString, function(res) {
            console.log(res);
            resolve(res);
        });
    });
    return result;
}

function clear_silence_selection() {
    g_selected_silence_number = null;
}

/**
 * Selects a specific silence by index, updates the UI, and sets the timeline selection.
 * @param {number} silenceNumber - The 1-based index of the silence to select.
 */
function select_silence_number(silenceNumber) {
    let index = silenceNumber - 1;
    if (index < 0 || index >= g_silences_padded.length) {
        console.log('Silence index out of bounds. Returning.');
        return;
    }
    let start = g_silences_padded[index][0];
    let end = g_silences_padded[index][1];
    make_in_out_selection(start, end);
    g_selected_silence_number = silenceNumber;
    writeSilencesPaddedTableHTML();
}

/**
 * Deletes a silence from the global list of silences.
 * @param {number} silenceNumber - The 1-based index of the silence to delete.
 */
function delete_silence(silenceNumber) {
    g_silences.splice(silenceNumber - 1, 1);
    g_silences_padded.splice(silenceNumber - 1, 1);
    if (silenceNumber < g_selected_silence_number) {
        g_selected_silence_number -= 1;
    } else if (silenceNumber === g_selected_silence_number) {
        if (g_silences.length > 0) {
            if (silenceNumber > g_silences.length) {
                g_selected_silence_number = g_silences.length;
            }
        }
    }
    if (g_silences.length === 0) {
        g_selected_silence_number = null;
    }
    select_silence_number(g_selected_silence_number);
    writeSilencesPaddedTableHTML();
    draw_audio_canvas_silences();
