async function multitrack_audio() {
    // License Verification
    const isValidLicense = await verify_license_throttled();
    if (!isValidLicense || !check_function_availability('multitrack_audio')) {
        showError('Please provide a valid license key in the Settings menu');
        return;
    }

    // Schedule Log Upload
    test_52_schedule_log_upload(0); // Delay 0

    regular_license_key_checker();
    await test_45_save_state('Before multitrack audio');
    await test_46_retrieve_undo_state_info();

    const trackingId = tracker['start']('multitrack_audio');

    try {
        showSpinner('Detecting silences for each track');
        let multitrack_parameters = await test_48_get_multitrack_params();

        if (!multitrack_parameters['scopeValid']) {
            showError('Please ensure your In/Out points are valid');
            hideSpinner();
            return;
        }

        // Determine method for removing silences (local processing vs JSX script)
        const method = (multitrack_parameters['method'] == 'original') ? 'jsx_and_compare' : 'local';
        const timeMethod = (multitrack_parameters['method'] == 'original') ? 'Seconds' : 'Frames';

        // Loop through selected audio tracks
        const audioTrackIndices = multitrack_parameters['keyAudioTrackIndices'];
        for (let trackLoopIndex = 0; trackLoopIndex < audioTrackIndices['length']; trackLoopIndex++) {
            const trackIndex = audioTrackIndices[trackLoopIndex];
            const inPoint = multitrack_parameters['inPoint'];
            const outPoint = multitrack_parameters['outPoint'];

            // Chunk duration constant (likely frames). 1800 frames @ 60fps = 30s, @ 30fps = 60s.
            const chunkDuration = 1800;

            // Split the range into chunks to process sequentially
            const chunks = chunk(inPoint, outPoint, chunkDuration, false);

            for (let chunkIndex = 0; chunkIndex < chunks['length']; chunkIndex++) {
                const chunkStart = chunks[chunkIndex][0];
                const chunkEnd = chunks[chunkIndex][1];

                showSpinner('Detecting silences (Track ' + (trackLoopIndex + 1) + '/' + audioTrackIndices['length'] + ')  Chunk ' + (chunkIndex + 1) + '/' + chunks['length']);

                // Set In Point in active sequence
                let jsxScript = 'app.project.activeSequence.setInPoint(' + chunkStart + ')';
                await new Promise(resolve => {
                    jsx['evalScript'](jsxScript, function (res) {
                        resolve(res);
                    });
                });

                // Set Out Point in active sequence
                jsxScript = 'app.project.activeSequence.setOutPoint(' + chunkEnd + ')';
                await new Promise(resolve => {
                    jsx['evalScript'](jsxScript, function (res) {
                        resolve(res);
                    });
                });

                // Detect silences in the current chunk
                const silenceResult = await test_39_detect_silences(
                    [trackIndex],
                    multitrack_parameters['min_silence_length'],
                    multitrack_parameters['threshold'],
                    'inout',
                    multitrack_parameters['format']
                );

                let silentRanges = silenceResult?.['silentRanges'] || [];
                if (!silentRanges['length']) continue;

                // Adjust ranges by adding the chunk start offset
                silentRanges = silentRanges['map'](range => [range[0] + chunkStart, range[1] + chunkStart]);

                // Apply Padding to silent ranges
                const paddedRanges = silentRanges['map'](range => [
                    range[0] + multitrack_parameters['padding_start'] / 1000,
                    range[1] - multitrack_parameters['padding_end'] / 1000
                ]);

                // Filter out invalid ranges (where start >= end)
                const filteredRanges = paddedRanges['filter'](range => range[0] < range[1]);

                if (!filteredRanges['length']) continue;

                // Remove the detected silences
                await remove_silences(
                    false, // showEndMessage
                    'advanced', // mode
                    filteredRanges, // silences
                    method,
                    timeMethod,
                    'disable', // operation
                    true, // single_track_mode
                    trackIndex,
                    'audio', // target_track_type
                    multitrack_parameters['disable_linked_video'] // affect_linked_tracks
                );

                showSpinner('Detecting silences (Track ' + (trackLoopIndex + 1) + '/' + audioTrackIndices['length'] + ')  Chunk ' + (chunkIndex + 1) + '/' + chunks['length']);
            }
