function moveAllClipsInTrack(track_index, track_type, move_amount_in_seconds) {
    if (track_type == "video") { 
        var clips = app.project.activeSequence.videoTracks[track_index].clips;
    } else if (track_type == "audio") { 
        var clips = app.project.activeSequence.audioTracks[track_index].clips;
    }

    // Move from end to start if positive, start to end if negative
    // This prevents clip collision during movement
    if (move_amount_in_seconds > 0) { 
        var i = clips.length - 1;
        var increment = -1;
    } else {
        var i = 0;
        var increment = 1;
    }

    for (i; (i < clips.length) && (i >= 0); i += increment) { 
        var clip = clips[i];
        clip.move(move_amount_in_seconds);
    }
}
