    64→// =============================================================================
    65→// PERF-FIX: Static Response Cache
    66→// Cache static endpoint responses at startup to avoid repeated computation
    67→// =============================================================================
    68→
    69→// Generate ETag from content
    70→function generateETag(content) {
    71→  return `"${crypto.createHash('md5').update(JSON.stringify(content)).digest('hex')}"`;
    72→}
    73→
    74→// Static response cache - populated at startup
    75→const STATIC_RESPONSE_CACHE = {
    76→  profanityLanguages: null,
    77→  profanityBleeps: null,
    78→  exportFormats: null
    79→};
    80→
    81→// Initialize cache at startup (called after server starts)
    82→function initializeStaticCache() {
    83→  // Cache profanity languages
    84→  const languages = getSupportedLanguages();
    85→  STATIC_RESPONSE_CACHE.profanityLanguages = {
    86→    body: { success: true, languages },
    87→    etag: generateETag({ languages })
    88→  };
    89→
    90→  // Cache profanity bleeps
    91→  const sounds = getAvailableBleepSounds();
    92→  STATIC_RESPONSE_CACHE.profanityBleeps = {
    93→    body: { success: true, sounds },
    94→    etag: generateETag({ sounds })
    95→  };
    96→
    97→  // Cache export formats
    98→  const formats = getSupportedFormats();
    99→  STATIC_RESPONSE_CACHE.exportFormats = {
   100→    body: { success: true, formats },
   101→    etag: generateETag({ formats })
   102→  };
   103→
   104→  console.log('[SPLICE] Static response cache initialized');
   105→}
   106→
   107→// Helper to send cached response with ETag
   108→function sendCachedResponse(req, res, cacheKey) {
   109→  const cached = STATIC_RESPONSE_CACHE[cacheKey];
   110→  if (!cached) {
   111→    return res.status(500).json({ error: 'Cache not initialized' });
   112→  }
   113→
   114→  // Check If-None-Match header for conditional GET
   115→  const clientETag = req.headers['if-none-match'];
   116→  if (clientETag === cached.etag) {
   117→    return res.status(304).end(); // Not Modified
   118→  }
   119→
   120→  // Send cached response with ETag and cache headers
   121→  res.set('ETag', cached.etag);
   122→  res.set('Cache-Control', 'public, max-age=86400'); // Cache for 24 hours
   123→  res.json(cached.body);
   124→}
