   126→// =============================================================================
   127→// Server Configuration
   128→// =============================================================================
   129→
   130→const app = express();
   131→const PORT = process.env.PORT || 3847;
   132→
   133→// HTTPS certificates (generated by mkcert) - only for local development
   134→let httpsOptions = null;
   135→if (!isProduction) {
   136→  const keyPath = path.join(__dirname, 'localhost+1-key.pem');
   137→  const certPath = path.join(__dirname, 'localhost+1.pem');
   138→  if (fs.existsSync(keyPath) && fs.existsSync(certPath)) {
   139→    httpsOptions = {
   140→      key: fs.readFileSync(keyPath),
   141→      cert: fs.readFileSync(certPath)
   142→    };
   143→  }
   144→}
   145→
   146→// =============================================================================
   147→// Security Configuration
   148→// =============================================================================
   149→
   150→// CORS whitelist - restrict origins in production
   151→const CORS_WHITELIST = [
   152→  'http://localhost:3000',
   153→  'http://localhost:3847',
   154→  'https://localhost:3847',
   155→  'http://127.0.0.1:3000',
   156→  'http://127.0.0.1:3847',
   157→  'https://127.0.0.1:3847',
   158→  'https://splice.app',
   159→  'https://www.splice.app',
   160→  'https://splice-api-production.up.railway.app',
   161→  // Adobe CEP/UXP panels run from file:// or bolt://
   162→  'file://',
   163→  'bolt://'
   164→];
   165→
   166→const corsOptions = {
   167→  origin: function (origin, callback) {
   168→    // Allow requests with no origin (like mobile apps, Postman, or Adobe plugins)
   169→    if (!origin) {
   170→      return callback(null, true);
   171→    }
   172→    // Check if origin is in whitelist
   173→    if (CORS_WHITELIST.some(allowed => origin.startsWith(allowed) || origin === allowed)) {
   174→      return callback(null, true);
   175→    }
   176→    // In development, allow all origins with warning
   177→    if (!isProduction) {
   178→      console.warn(`[CORS] Non-whitelisted origin in dev: ${origin}`);
   179→      return callback(null, true);
   180→    }
   181→    // In production, reject non-whitelisted origins
   182→    console.error(`[CORS] Blocked request from: ${origin}`);
   183→    return callback(new Error('Not allowed by CORS'));
   184→  },
   185→  credentials: true,
   186→  methods: ['GET', 'POST', 'PUT', 'DELETE', 'OPTIONS'],
   187→  allowedHeaders: ['Content-Type', 'Authorization', 'x-stripe-customer-id', 'stripe-signature']
   188→};
   189→
   190→app.use(cors(corsOptions));
   191→
   192→// Security headers via helmet
   193→app.use(helmet({
   194→  contentSecurityPolicy: {
   195→    directives: {
   196→      defaultSrc: ["'self'"],
   197→      scriptSrc: ["'self'"],
   198→      styleSrc: ["'self'", "'unsafe-inline'"],
   199→      imgSrc: ["'self'", 'data:', 'https:'],
   200→      connectSrc: ["'self'", 'https://api.stripe.com', 'https://api.openai.com', 'https://api.replicate.com'],
   201→      fontSrc: ["'self'"],
   202→      objectSrc: ["'none'"],
   203→      mediaSrc: ["'self'"],
   204→      frameSrc: ["'none'"]
   205→    }
   206→  },
   207→  hsts: {
   208→    maxAge: 31536000,
   209→    includeSubDomains: true,
   210→    preload: true
   211→  },
   212→  referrerPolicy: { policy: 'strict-origin-when-cross-origin' },
   213→  noSniff: true,
   214→  xssFilter: true,
   215→  hidePoweredBy: true
   216→}));
   217→
   218→// Helper to determine tier from price ID with logging
   219→function getTierFromPriceId(priceId) {
   220→  if (priceId === process.env.STRIPE_PRICE_STARTER) return 'starter';
   221→  if (priceId === process.env.STRIPE_PRICE_PRO) return 'pro';
   222→  if (priceId === process.env.STRIPE_PRICE_TEAM) return 'team';
   223→
   224→  // Log unknown price ID for debugging
   225→  console.warn(`[SPLICE] Unknown price ID: ${priceId} - defaulting to starter tier`);
   226→  return 'starter';
   227→}
   228→
   229→// =============================================================================
   230→// Stripe Webhook (must be before express.json())
   231→// =============================================================================
   232→
   233→app.post('/webhooks/stripe', express.raw({ type: 'application/json' }), async (req, res) => {
   234→  const sig = req.headers['stripe-signature'];
   235→  const webhookSecret = process.env.STRIPE_WEBHOOK_SECRET;
   236→
   237→  let event;
   238→
   239→  try {
   240→    if (webhookSecret) {
   241→      event = stripe.webhooks.constructEvent(req.body, sig, webhookSecret);
   242→    } else if (isProduction) {
   243→      // SECURITY: Reject unsigned webhooks in production
   244→      console.error('[SPLICE] CRITICAL: STRIPE_WEBHOOK_SECRET not set in production');
   245→      return res.status(500).json({ error: 'Webhook configuration error: secret not configured' });
   246→    } else {
   247→      // For local development testing only
   248→      // req.body is a Buffer from express.raw(), convert to string for JSON.parse
   249→      const bodyString = typeof req.body === 'string' ? req.body : req.body.toString('utf8');
   250→      event = JSON.parse(bodyString);
   251→      console.warn('[SPLICE] Warning: Processing webhook without signature verification (dev only)');
   252→    }
   253→  } catch (err) {
   254→    console.error('[SPLICE] Webhook signature verification failed:', err.message);
   255→    return res.status(400).json({ error: 'Webhook signature verification failed' });
   256→  }
   257→
   258→  console.log(`[SPLICE] Webhook received: ${event.type} (${event.id})`);
   259→
   260→  // Idempotency check - skip if already processed
   261→  if (await usageTracking.isEventProcessed(event.id)) {
   262→    console.log(`[SPLICE] Event ${event.id} already processed, skipping`);
   263→    return res.json({ received: true, skipped: true });
   264→  }
   265→
   266→  try {
   267→    switch (event.type) {
   268→      case 'customer.subscription.created':
   269→      case 'customer.subscription.updated': {
   270→        const subscription = event.data.object;
   271→        const customerId = subscription.customer;
   272→
   273→        // Validate customerId
   274→        if (!customerId) {
   275→          console.error('[SPLICE] Missing customer ID in subscription event');
   276→          return res.status(400).json({ error: 'Missing customer ID' });
   277→        }
   278→
   279→        // Get tier from price ID
   280→        const priceId = subscription.items?.data?.[0]?.price?.id;
   281→        const tier = getTierFromPriceId(priceId);
   282→
   283→        // Update user tier and reset hours
   284→        await usageTracking.updateTier(customerId, tier);
   285→        console.log(`[SPLICE] Updated customer ${customerId} to tier: ${tier}`);
   286→
   287→        // Generate license key for new subscriptions with retry and delivery
   288→        if (event.type === 'customer.subscription.created') {
   289→          let licenseResult = null;
   290→          let retryCount = 0;
   291→          const maxRetries = 3;
   292→
   293→          // Retry mechanism for license key generation
   294→          while (retryCount < maxRetries) {
   295→            licenseResult = await licenseService.generateLicenseKey(customerId);
   296→            if (licenseResult.success) {
   297→              break;
   298→            }
   299→            retryCount++;
   300→            console.warn(`[SPLICE] License key generation attempt ${retryCount}/${maxRetries} failed: ${licenseResult.error}`);
   301→            // Wait before retry (exponential backoff)
   302→            if (retryCount < maxRetries) {
   303→              await new Promise(resolve => setTimeout(resolve, 1000 * retryCount));
   304→            }
   305→          }
   306→
   307→          if (licenseResult && licenseResult.success) {
   308→            // SECURITY: Mask license key in logs
   309→            console.log(`[SPLICE] Generated license key for ${maskSensitiveData(customerId)}: ${maskSensitiveData(licenseResult.key)}`);
   310→
   311→            // Store license key in Stripe subscription metadata as backup
   312→            try {
   313→              await stripe.subscriptions.update(subscription.id, {
   314→                metadata: {
   315→                  license_key: licenseResult.key,
   316→                  license_generated_at: new Date().toISOString()
   317→                }
   318→              });
   319→              console.log(`[SPLICE] Stored license key in Stripe metadata for subscription ${subscription.id}`);
   320→            } catch (metaErr) {
   321→              console.error(`[SPLICE] Failed to store license key in Stripe metadata:`, metaErr.message);
   322→            }
   323→
   324→            // Get customer email and send license key
   325→            try {
   326→              const customer = await stripe.customers.retrieve(customerId);
   327→              if (customer.email) {
   328→                // SECURITY: Mask sensitive data in logs
   329→                console.log(`[SPLICE] License key ready for delivery to ${maskSensitiveData(customer.email)}: ${maskSensitiveData(licenseResult.key)}`);
   330→                // TODO: Integrate with email service (SendGrid, SES, etc.)
   331→                // await sendLicenseKeyEmail(customer.email, licenseResult.key, tier);
   332→
   333→                // Store email in database for reference
   334→                await usageTracking.updateTier(customerId, tier, customer.email);
   335→              } else {
   336→                console.warn(`[SPLICE] No email found for customer ${customerId}`);
   337→              }
   338→            } catch (emailErr) {
   339→              console.error(`[SPLICE] Error getting customer email:`, emailErr.message);
   340→            }
   341→          } else {
   342→            // CRITICAL: License generation failed - return 500 to trigger Stripe retry
   343→            const errorMsg = `Failed to generate license key after ${maxRetries} attempts: ${licenseResult?.error || 'Unknown error'}`;
   344→            console.error(`[SPLICE] ${errorMsg}`);
   345→            return res.status(500).json({ error: errorMsg });
   346→          }
   347→        }
   348→        break;
   349→      }
   350→
   351→      case 'customer.subscription.deleted': {
   352→        const subscription = event.data.object;
   353→        const customerId = subscription.customer;
   354→
   355→        // Validate customerId
   356→        if (!customerId) {
   357→          console.error('[SPLICE] Missing customer ID in subscription.deleted event');
   358→          return res.status(400).json({ error: 'Missing customer ID' });
   359→        }
   360→
   361→        // Downgrade to cancelled (0 hours)
   362→        await usageTracking.updateTier(customerId, 'cancelled');
   363→        console.log(`[SPLICE] Subscription cancelled for customer ${customerId}`);
   364→        break;
   365→      }
   366→
   367→      case 'invoice.payment_succeeded': {
   368→        const invoice = event.data.object;
   369→        const customerId = invoice.customer;
   370→        const subscriptionId = invoice.subscription;
   371→
   372→        // Validate customerId
   373→        if (!customerId) {
   374→          console.error('[SPLICE] Missing customer ID in invoice event');
   375→          return res.status(400).json({ error: 'Missing customer ID' });
   376→        }
   377→
   378→        // Reset hours on successful payment (new billing period)
   379→        if (subscriptionId) {
   380→          const subscription = await stripe.subscriptions.retrieve(subscriptionId);
   381→          const priceId = subscription.items?.data?.[0]?.price?.id;
   382→          const tier = getTierFromPriceId(priceId);
   383→
   384→          await usageTracking.resetHours(customerId, tier);
   385→          console.log(`[SPLICE] Reset hours for customer ${customerId} (tier: ${tier})`);
   386→        }
   387→        break;
   388→      }
   389→
   390→      default:
   391→        console.log(`[SPLICE] Unhandled event type: ${event.type}`);
   392→    }
   393→
   394→    // Record event as processed (idempotency)
   395→    await usageTracking.recordWebhookEvent(event.id, event.type);
   396→
   397→    res.json({ received: true });
   398→  } catch (err) {
   399→    console.error('[SPLICE] Webhook handler error:', err);
   400→    res.status(500).json({ error: err.message });
   401→  }
   402→});
   403→
