   407→// =============================================================================
   408→// Route Configuration Options
   409→// =============================================================================
   410→
   411→const routeOptions = {
   412→  middleware: {
   413→    requireCredits,
   414→    authenticateToken
   415→  },
   416→  services: {
   417→    usageTracking,
   418→    referralService,
   419→    licenseService,
   420→    stripe
   421→  },
   422→  authHelpers: {
