   448→app.use('/multitrack', multitrackRoutes(routeOptions));
   449→
   450→// Cut list generation
   451→app.use('/cut-list', cutListRoutes(routeOptions));
   452→
   453→// Legacy XML processing endpoint (backwards compatibility)
   454→const { processXMLFile } = require('./services/xmlProcessor');
   455→app.post('/process-xml', requireCredits({ endpoint: 'process-xml' }), async (req, res) => {
   456→  const {
   457→    xmlPath,
   458→    silences,
   459→    removeGaps = true,
   460→    outputPath = null
   461→  } = req.body;
   462→
   463→  if (!xmlPath) {
   464→    return res.status(400).json({ error: 'xmlPath is required' });
   465→  }
   466→
   467→  if (!silences || !Array.isArray(silences)) {
   468→    return res.status(400).json({ error: 'silences array is required' });
   469→  }
   470→
   471→  if (!fs.existsSync(xmlPath)) {
   472→    return res.status(404).json({ error: `XML file not found: ${xmlPath}` });
   473→  }
   474→
   475→  console.log(`[SPLICE] Processing XML: ${xmlPath} with ${silences.length} silence(s)`);
   476→
   477→  try {
   478→    const result = await processXMLFile(xmlPath, silences, {
   479→      outputPath,
   480→      removeGaps
   481→    });
   482→
   483→    res.json({
   484→      success: true,
   485→      inputPath: xmlPath,
   486→      outputPath: result.outputPath,
   487→      stats: result.stats
   488→    });
   489→  } catch (err) {
   490→    console.error('[SPLICE] XML processing error:', err);
   491→    res.status(500).json({ error: err.message });
   492→  }
   493→});
   494→
