   512→app.use('/faces', reframeRoutes.facesRouter(routeOptions));
   513→
   514→// Batch processing endpoints
   515→app.use('/batch', batchRoutes(routeOptions));
   516→
   517→// Authentication endpoints
   518→app.use('/auth', authRoutes(routeOptions));
   519→
   520→// Billing and credits endpoints
   521→app.use('/', billingRoutes(routeOptions));
   522→
   523→// Referral system endpoints
