   463→
   464→async function runDetection() {
   465→    if (isOperationInProgress) return;
   466→
   467→    // Check sequence
   468→    const hasSequence = await checkSequenceOpen();
   469→    if (!hasSequence) {
   470→        setStatus('Please open a sequence first', true);
   471→        return;
   472→    }
   473→
   474→    // Check online
   475→    if (!isOnline()) {
   476→        setStatus('Offline - Check your connection', true);
   477→        return;
   478→    }
   479→
   480→    isOperationInProgress = true;
   481→    ui.goBtn.disabled = true;
   482→    showProgress('Detecting silences...');
   483→
   484→    try {
   485→        const settings = getSettings();
   486→
   487→        // Get sequence info
   488→        const seqInfo = await jsx.call('getActiveSequence');
   489→        console.log('[SPLICE] Sequence info:', seqInfo);
   490→
   491→        // Store sequence info for later use
   492→        currentSequenceInfo = seqInfo;
   493→        window.spliceState.activeSequence = seqInfo;
   494→        if (seqInfo && seqInfo.videoPath) {
   495→            window.spliceState.currentVideoPath = seqInfo.videoPath;
   496→        }
   497→
   498→        // Export audio via JSX for silence detection
   499→        updateProgress(10, 'Exporting audio...');
   500→
   501→        let audioFilePath = null;
   502→        try {
   503→            // Get sequence info for audio export
   504→            const exportResult = await jsx.call('exportSequenceAudioForAnalysis');
   505→            if (exportResult && exportResult.success && exportResult.outputPath) {
   506→                audioFilePath = exportResult.outputPath;
   507→                console.log('[SPLICE] Audio exported to:', audioFilePath);
   508→            } else if (exportResult && exportResult.error) {
   509→                throw new Error(exportResult.error);
   510→            }
   511→        } catch (exportError) {
   512→            console.warn('[SPLICE] Audio export failed, trying fallback:', exportError.message);
   513→            // Try to get audio from first clip in timeline
   514→            try {
   515→                const clipInfo = await jsx.call('getFirstClipAudioPath');
   516→                if (clipInfo && clipInfo.path) {
   517→                    audioFilePath = clipInfo.path;
   518→                    console.log('[SPLICE] Using clip audio path:', audioFilePath);
   519→                }
   520→            } catch (clipError) {
   521→                console.warn('[SPLICE] Fallback audio path failed:', clipError.message);
   522→            }
   523→        }
   524→
   525→        if (!audioFilePath) {
   526→            throw new Error('Could not export or locate audio file. Please ensure sequence has audio tracks.');
   527→        }
   528→
   529→        // Store audio path globally for other modules
   530→        currentAudioPath = audioFilePath;
   531→        window.spliceState.audioPath = audioFilePath;
   532→
   533→        // Call backend for silence detection with audio file path
   534→        updateProgress(30, 'Detecting silences...');
   535→        const silenceResponse = await fetchWithTimeout(
   536→            `${getBackendUrl()}/silences-rms`,
   537→            {
   538→                method: 'POST',
   539→                headers: getAuthHeaders(),
   540→                body: JSON.stringify({
   541→                    wavPath: audioFilePath,
   542→                    sensitivity: settings.sensitivity,
   543→                    minSilenceLength: settings.minSilenceLength || 0.5
   544→                })
   545→            }
   546→        );
   547→
   548→        if (!silenceResponse.ok) {
   549→            throw new Error(await parseErrorResponse(silenceResponse));
   550→        }
   551→
   552→        const silenceData = await silenceResponse.json();
   553→        previewSilences = silenceData.silences || [];
   554→
   555→        // Detect takes if enabled
   556→        if (settings.enableTakesDetection) {
   557→            updateProgress(60, 'Detecting takes...');
   558→            const analyzeResponse = await fetchWithTimeout(
   559→                `${getBackendUrl()}/analyze`,
   560→                {
   561→                    method: 'POST',
   562→                    headers: getAuthHeaders(),
   563→                    body: JSON.stringify({
   564→                        wavPath: audioFilePath,
   565→                        detectTakes: true
   566→                    })
   567→                }
   568→            );
   569→
   570→            if (analyzeResponse.ok) {
   571→                const analyzeData = await analyzeResponse.json();
   572→                previewTakes = analyzeData.takes || [];
   573→
   574→                // Store transcript for captions/music modules
   575→                if (analyzeData.transcript) {
   576→                    currentTranscript = analyzeData.transcript;
   577→                    window.currentTranscript = analyzeData.transcript;
   578→                    window.spliceState.lastTranscript = analyzeData.transcript;
   579→                }
   580→            }
   581→        }
   582→
   583→        // Detect chapters if enabled
   584→        if (settings.enableChapters) {
   585→            updateProgress(80, 'Detecting chapters...');
   586→            try {
   587→                const chapterResponse = await fetchWithTimeout(
   588→                    `${getBackendUrl()}/chapters`,
   589→                    {
   590→                        method: 'POST',
   591→                        headers: getAuthHeaders(),
   592→                        body: JSON.stringify({
   593→                            wavPath: audioFilePath,
   594→                            transcript: currentTranscript,
   595→                            maxChapters: settings.maxChapters || 10,
   596→                            minChapterLength: settings.minChapterLength || 60
   597→                        })
   598→                    }
   599→                );
   600→
   601→                if (chapterResponse.ok) {
   602→                    const chapterData = await chapterResponse.json();
   603→                    currentChapters = chapterData.chapters || [];
   604→                }
   605→            } catch (e) {
   606→                console.warn('[SPLICE] Chapter detection failed:', e);
   607→            }
   608→        }
   609→
   610→        // Detect zoom points if enabled
   611→        if (settings.enableZoom) {
   612→            updateProgress(90, 'Detecting zoom points...');
   613→            try {
   614→                const zoomResponse = await fetchWithTimeout(
   615→                    `${getBackendUrl()}/zoom`,
   616→                    {
   617→                        method: 'POST',
   618→                        headers: getAuthHeaders(),
   619→                        body: JSON.stringify({
   620→                            wavPath: audioFilePath,
   621→                            transcript: currentTranscript,
   622→                            frequency: settings.zoomFrequency || 'medium',
   623→                            preset: settings.zoomPreset || 'medium',
   624→                            placement: settings.zoomPlacement || 'sentence_start'
   625→                        })
   626→                    }
   627→                );
   628→
   629→                if (zoomResponse.ok) {
   630→                    const zoomData = await zoomResponse.json();
   631→                    currentZooms = zoomData.zoomPoints || [];
   632→                }
   633→            } catch (e) {
   634→                console.warn('[SPLICE] Zoom detection failed:', e);
   635→            }
   636→        }
   637→
   638→        // Store results
   639→        lastDetectionResult = {
   640→            silences: previewSilences,
   641→            takes: previewTakes,
   642→            chapters: currentChapters,
   643→            zooms: currentZooms
   644→        };
   645→
   646→        // Show preview
   647→        hideProgress();
   648→        showCombinedPreview();
   649→        setStatus(`Found ${previewSilences.length} silences, ${previewTakes.length} takes`);
   650→
   651→    } catch (error) {
   652→        console.error('[SPLICE] Detection error:', error);
   653→        setStatus('Detection failed: ' + error.message, true);
   654→        hideProgress();
   655→    } finally {
   656→        isOperationInProgress = false;
   657→        ui.goBtn.disabled = false;
   658→    }
   659→}
