   713→function renderPreviewList() {
   714→    if (!ui.previewList) return;
   715→
   716→    const html = [];
   717→
   718→    // Render silences
   719→    previewSilences.forEach((silence, i) => {
   720→        const isSelected = selectedSilenceIndices.has(i);
   721→        const duration = silence.end - silence.start;
   722→        html.push(`
   723→            <div class="preview-item ${isSelected ? '' : 'excluded'}" data-index="${i}" data-type="silence">
   724→                <input type="checkbox" class="preview-item-check" ${isSelected ? 'checked' : ''}>
   725→                <div class="preview-item-info">
   726→                    <div class="preview-item-time">${formatTime(silence.start)} - ${formatTime(silence.end)}</div>
   727→                    <div class="preview-item-duration">${duration.toFixed(2)}s silence</div>
   728→                </div>
   729→            </div>
   730→        `);
   731→    });
   732→
   733→    // Render takes
   734→    previewTakes.forEach((take, i) => {
   735→        html.push(`
   736→            <div class="preview-item take-item" data-index="${i}" data-type="take">
   737→                <div class="preview-item-icon">🎬</div>
   738→                <div class="preview-item-info">
   739→                    <div class="preview-item-time">${formatTime(take.start)} - ${formatTime(take.end)}</div>
   740→                    <div class="preview-item-label">${take.label || 'Take ' + (take.takeNumber || i + 1)}</div>
   741→                </div>
   742→            </div>
   743→        `);
   744→    });
   745→
   746→    ui.previewList.innerHTML = html.join('');
   747→
   748→    // Add click handlers
   749→    ui.previewList.querySelectorAll('.preview-item').forEach(item => {
   750→        item.addEventListener('click', (e) => {
   751→            const index = parseInt(item.dataset.index);
   752→            const type = item.dataset.type;
   753→
   754→            if (e.target.classList.contains('preview-item-check')) {
   755→                // Checkbox clicked
   756→                toggleSilenceSelection(index);
   757→            } else {
   758→                // Item clicked - seek to time
   759→                const data = type === 'silence' ? previewSilences[index] : previewTakes[index];
   760→                seekToTime(data.start);
   761→            }
   762→        });
   763→    });
   764→}
   765→
   766→function toggleSilenceSelection(index) {
   767→    if (selectedSilenceIndices.has(index)) {
   768→        selectedSilenceIndices.delete(index);
   769→    } else {
   770→        selectedSilenceIndices.add(index);
   771→    }
   772→    updateSelectedCount();
   773→    renderPreviewList();
   774→}
   775→
   776→function updateSelectedCount() {
   777→    if (ui.selectedCount) {
   778→        ui.selectedCount.textContent = selectedSilenceIndices.size;
   779→    }
   780→    if (ui.selectAllSilences) {
   781→        ui.selectAllSilences.checked = selectedSilenceIndices.size === previewSilences.length;
   782→    }
   783→}
