   785→async function seekToTime(seconds) {
   786→    try {
   787→        // Use JSX to set playhead position
   788→        // Note: setPlayerPosition expects ticks as a string without quotes around the number value
   789→        const ticks = Math.round(seconds * 254016000000);
   790→        await jsx.evalScript(`app.project.activeSequence.setPlayerPosition(${ticks})`);
   791→    } catch (e) {
   792→        console.warn('[SPLICE] Seek failed:', e);
   793→    }
   794→}
