   363→/**
   364→ * Check if a sequence is currently open in the Timeline
   365→ * Enhanced with logging and retry logic
   366→ * @param {number} retries - Number of retry attempts (default: 2)
   367→ * @param {number} delayMs - Delay between retries in ms (default: 300)
   368→ * @returns {Promise<boolean>} true if sequence is open
   369→ */
   370→async function checkSequenceOpen(retries = 2, delayMs = 300) {
   371→    console.log('[SPLICE] ========== checkSequenceOpen DIAGNOSTIC START ==========');
   372→    console.log('[SPLICE] checkSequenceOpen: retries=' + retries + ', delayMs=' + delayMs);
   373→    console.log('[SPLICE] checkSequenceOpen: window.spliceState.activeSequence =', window.spliceState.activeSequence);
   374→    
   375→    for (let attempt = 0; attempt <= retries; attempt++) {
   376→        try {
   377→            console.log('[SPLICE] checkSequenceOpen: Attempt ' + (attempt + 1) + ' of ' + (retries + 1));
   378→            console.log('[SPLICE] checkSequenceOpen: Calling jsx.call("checkSequenceOpen")...');
   379→            const result = await jsx.call('checkSequenceOpen');
   380→            console.log('[SPLICE] checkSequenceOpen: JSX call returned');
   381→            console.log('[SPLICE] checkSequenceOpen: result =', result, '(type:', typeof result + ')');
   382→            debugLog('checkSequenceOpen attempt', attempt + 1, 'result:', result, 'type:', typeof result);
   383→
   384→            if (result === true || result === 'true') {
   385→                console.log('[SPLICE] checkSequenceOpen: Returning TRUE - sequence is open');
   386→                console.log('[SPLICE] ========== checkSequenceOpen DIAGNOSTIC END (TRUE) ==========');
   387→                return true;
   388→            }
   389→
   390→            // If false, wait and retry (sequence might be loading)
   391→            if (attempt < retries) {
   392→                console.log('[SPLICE] checkSequenceOpen: No sequence found, retrying in', delayMs, 'ms...');
   393→                debugLog('checkSequenceOpen: No sequence found, retrying in', delayMs, 'ms...');
   394→                await new Promise(r => setTimeout(r, delayMs));
   395→            }
   396→        } catch (error) {
   397→            console.error('[SPLICE] checkSequenceOpen: Attempt', attempt + 1, 'failed with exception:', error.message);
   398→            console.error('[SPLICE] checkSequenceOpen: Error stack:', error.stack);
   399→
   400→            // On last attempt, return false but log the actual error
   401→            if (attempt === retries) {
   402→                console.error('[SPLICE] checkSequenceOpen: All attempts failed. Last error:', error.message);
   403→                // Store error for diagnostics
   404→                window.spliceState.lastSequenceCheckError = error.message;
   405→                console.log('[SPLICE] checkSequenceOpen: Stored error in window.spliceState.lastSequenceCheckError:', error.message);
   406→                console.log('[SPLICE] ========== checkSequenceOpen DIAGNOSTIC END (ERROR) ==========');
   407→                return false;
   408→            }
   409→
   410→            // Wait before retry
   411→            console.log('[SPLICE] checkSequenceOpen: Waiting', delayMs, 'ms before retry...');
   412→            await new Promise(r => setTimeout(r, delayMs));
   413→        }
   414→    }
   415→    console.log('[SPLICE] checkSequenceOpen: All attempts completed without finding sequence');
   416→    console.log('[SPLICE] ========== checkSequenceOpen DIAGNOSTIC END (FALSE) ==========');
   417→    return false;
   418→}
