   423→function initOptionsToggle() {
   424→    debugLog('initOptionsToggle - toggle:', !!ui.optionsToggle, 'panel:', !!ui.optionsPanel);
   425→    if (ui.optionsToggle && ui.optionsPanel) {
   426→        ui.optionsToggle.addEventListener('click', () => {
   427→            debugLog('Options toggle clicked');
   428→            // Check current state BEFORE toggling
   429→            const wasCollapsed = ui.optionsPanel.classList.contains('collapsed');
   430→            ui.optionsPanel.classList.toggle('collapsed');
   431→            // If it was collapsed, it's now expanded (show 'expanded' class)
   432→            ui.optionsToggle.classList.toggle('expanded', wasCollapsed);
   433→            saveSettings({ expandedOptions: wasCollapsed });
   434→        });
   435→
   436→        // Restore state
   437→        const settings = getSettings();
   438→        if (!settings.expandedOptions) {
   439→            ui.optionsPanel.classList.add('collapsed');
   440→            ui.optionsToggle.classList.remove('expanded');
   441→        } else {
   442→            ui.optionsToggle.classList.add('expanded');
   443→            ui.optionsPanel.classList.remove('collapsed');
   444→        }
   445→    }
   446→}
