   733→async function runDetection() {
   734→    if (isOperationInProgress) return;
   735→
   736→    // Check sequence with enhanced error messaging
   737→    const hasSequence = await checkSequenceOpen();
   738→    if (!hasSequence) {
   739→        // Check if there was a communication error vs just no sequence
   740→        const lastError = window.spliceState.lastSequenceCheckError;
   741→        if (lastError && (lastError.includes('CEP') || lastError.includes('communication') || lastError.includes('evalScript'))) {
   742→            setStatus('Premiere Pro connection issue - try reopening panel', true);
   743→            console.error('[SPLICE] runDetection: JSX communication failure:', lastError);
   744→        } else {
   745→            setStatus('Please open a sequence in the Timeline first', true);
   746→            debugLog('runDetection: No active sequence in Timeline');
   747→        }
   748→        // Clear the error for next check
   749→        window.spliceState.lastSequenceCheckError = null;
   750→        return;
   751→    }
   752→
   753→    // Check online
   754→    if (!isOnline()) {
   755→        setStatus('Offline - Check your connection', true);
   756→        return;
   757→    }
   758→
   759→    isOperationInProgress = true;
   760→    ui.goBtn.disabled = true;
   761→    showProgress('Detecting silences...');
   762→
   763→    try {
   764→        const settings = getSettings();
   765→
   766→        // Get sequence info
   767→        const seqInfo = await jsx.call('getActiveSequence');
   768→        debugLog('Sequence info:', seqInfo);
   769→
   770→        // Store sequence info for later use
   771→        currentSequenceInfo = seqInfo;
   772→        window.spliceState.activeSequence = seqInfo;
   773→        if (seqInfo && seqInfo.videoPath) {
   774→            window.spliceState.currentVideoPath = seqInfo.videoPath;
   775→        }
   776→
   777→        // Export audio via JSX for silence detection
   778→        updateProgress(10, 'Exporting audio...');
   779→
   780→        let audioFilePath = null;
   781→        try {
   782→            // Get sequence info for audio export
   783→            const exportResult = await jsx.call('exportSequenceAudioForAnalysis');
   784→            if (exportResult && exportResult.success && exportResult.outputPath) {
   785→                audioFilePath = exportResult.outputPath;
   786→                debugLog('Audio exported to:', audioFilePath);
   787→            } else if (exportResult && exportResult.error) {
   788→                throw new Error(exportResult.error);
   789→            }
   790→        } catch (exportError) {
   791→            console.warn('[SPLICE] Audio export failed, trying fallback:', exportError.message);
   792→            // Try to get audio from first clip in timeline
   793→            try {
   794→                const clipInfo = await jsx.call('getFirstClipAudioPath');
   795→                if (clipInfo && clipInfo.path) {
   796→                    audioFilePath = clipInfo.path;
   797→                    debugLog('Using clip audio path:', audioFilePath);
   798→                }
   799→            } catch (clipError) {
   800→                console.warn('[SPLICE] Fallback audio path failed:', clipError.message);
   801→            }
   802→        }
   803→
   804→        if (!audioFilePath) {
   805→            throw new Error('Could not export or locate audio file. Please ensure sequence has audio tracks.');
   806→        }
   807→
   808→        // Store audio path globally for other modules
   809→        currentAudioPath = audioFilePath;
   810→        window.spliceState.audioPath = audioFilePath;
   811→
   812→        // Call backend for silence detection with audio file path
   813→        updateProgress(30, 'Detecting silences...');
   814→        const silenceResponse = await fetchWithTimeout(
   815→            `${getBackendUrl()}/silences-rms`,
   816→            {
   817→                method: 'POST',
   818→                headers: getAuthHeaders(),
   819→                body: JSON.stringify({
   820→                    wavPath: audioFilePath,
   821→                    sensitivity: settings.sensitivity,
   822→                    minSilenceLength: settings.minSilenceLength || 0.5
   823→                })
   824→            }
   825→        );
   826→
   827→        if (!silenceResponse.ok) {
   828→            throw new Error(await parseErrorResponse(silenceResponse));
   829→        }
   830→
   831→        const silenceData = await silenceResponse.json();
   832→        previewSilences = silenceData.silences || [];
   833→
   834→        // Detect takes if enabled
   835→        if (settings.enableTakesDetection) {
   836→            updateProgress(60, 'Detecting takes...');
   837→            try {
   838→                const analyzeResponse = await fetchWithTimeout(
   839→                    `${getBackendUrl()}/analyze`,
   840→                    {
   841→                        method: 'POST',
   842→                        headers: getAuthHeaders(),
   843→                        body: JSON.stringify({
   844→                            wavPath: audioFilePath,
   845→                            detectTakes: true
   846→                        })
   847→                    }
   848→                );
   849→
   850→                if (analyzeResponse.ok) {
   851→                    const analyzeData = await analyzeResponse.json();
   852→                    previewTakes = analyzeData.takes || [];
   853→
   854→                    // Store transcript for captions/music modules
   855→                    if (analyzeData.transcript) {
   856→                        currentTranscript = analyzeData.transcript;
   857→                        window.currentTranscript = analyzeData.transcript;
   858→                        window.spliceState.lastTranscript = analyzeData.transcript;
   859→                    }
   860→                } else {
   861→                    // Handle specific error codes
   862→                    const errorMsg = await parseErrorResponse(analyzeResponse);
   863→                    if (analyzeResponse.status === 402) {
   864→                        console.warn('[SPLICE] Takes detection skipped: insufficient credits');
   865→                        setStatus('Takes detection skipped: insufficient credits', true);
   866→                    } else if (analyzeResponse.status === 401) {
   867→                        console.warn('[SPLICE] Takes detection skipped: authentication required');
   868→                    } else {
   869→                        console.warn('[SPLICE] Takes detection failed:', errorMsg);
   870→                    }
   871→                }
   872→            } catch (e) {
   873→                console.warn('[SPLICE] Takes detection failed:', e.message);
   874→            }
   875→        }
   876→
   877→        // Detect profanity if enabled
   878→        if (settings.enableProfanity) {
   879→            updateProgress(65, 'Detecting profanity...');
   880→            try {
   881→                const profanityResponse = await fetchWithTimeout(
   882→                    `${getBackendUrl()}/profanity`,
   883→                    {
   884→                        method: 'POST',
   885→                        headers: getAuthHeaders(),
   886→                        body: JSON.stringify({
   887→                            wavPath: audioFilePath,
   888→                            language: settings.profanityLanguage || 'en',
   889→                            bleepType: settings.bleepType || 'standard'
   890→                        })
   891→                    }
   892→                );
   893→
   894→                if (profanityResponse.ok) {
   895→                    const profanityData = await profanityResponse.json();
   896→                    // Backend returns 'words' array, not 'instances'
   897→                    currentProfanity = profanityData.words || profanityData.instances || [];
   898→                    if (currentProfanity.length > 0) {
   899→                        debugLog(`Found ${currentProfanity.length} profanity instances`);
   900→                    }
   901→                } else {
   902→                    console.warn('[SPLICE] Profanity detection failed:', await parseErrorResponse(profanityResponse));
   903→                }
   904→            } catch (e) {
   905→                console.warn('[SPLICE] Profanity detection failed:', e.message);
   906→            }
   907→        }
   908→
   909→        // Detect chapters if enabled
   910→        if (settings.enableChapters) {
   911→            updateProgress(80, 'Detecting chapters...');
   912→            try {
   913→                const chapterResponse = await fetchWithTimeout(
   914→                    `${getBackendUrl()}/chapters`,
   915→                    {
   916→                        method: 'POST',
   917→                        headers: getAuthHeaders(),
   918→                        body: JSON.stringify({
   919→                            wavPath: audioFilePath,
   920→                            transcript: currentTranscript,
   921→                            maxChapters: settings.maxChapters || 10,
   922→                            minChapterLength: settings.minChapterLength || 60
   923→                        })
   924→                    }
   925→                );
   926→
   927→                if (chapterResponse.ok) {
   928→                    const chapterData = await chapterResponse.json();
   929→                    currentChapters = chapterData.chapters || [];
   930→                } else {
   931→                    const errorMsg = await parseErrorResponse(chapterResponse);
   932→                    console.warn('[SPLICE] Chapter detection failed:', errorMsg);
   933→                    // Don't block workflow, just notify user
   934→                    setStatus('Chapter detection skipped: ' + errorMsg, true);
   935→                }
   936→            } catch (e) {
   937→                console.warn('[SPLICE] Chapter detection failed:', e);
   938→                setStatus('Chapter detection failed - continuing without chapters', true);
   939→            }
   940→        }
   941→
   942→        // Detect zoom points if enabled
   943→        if (settings.enableZoom) {
   944→            updateProgress(90, 'Detecting zoom points...');
   945→            try {
   946→                const zoomResponse = await fetchWithTimeout(
   947→                    `${getBackendUrl()}/zoom`,
   948→                    {
   949→                        method: 'POST',
   950→                        headers: getAuthHeaders(),
   951→                        body: JSON.stringify({
   952→                            wavPath: audioFilePath,
   953→                            transcript: currentTranscript,
   954→                            frequency: settings.zoomFrequency || 'medium',
   955→                            preset: settings.zoomPreset || 'medium',
   956→                            placement: settings.zoomPlacement || 'sentence_start'
   957→                        })
   958→                    }
   959→                );
   960→
   961→                if (zoomResponse.ok) {
   962→                    const zoomData = await zoomResponse.json();
