  1050→function renderPreviewList() {
  1051→    if (!ui.previewList) return;
  1052→
  1053→    const html = [];
  1054→
  1055→    // Render silences
  1056→    previewSilences.forEach((silence, i) => {
  1057→        const isSelected = selectedSilenceIndices.has(i);
  1058→        const duration = silence.end - silence.start;
  1059→        html.push(`
  1060→            <div class="preview-item ${isSelected ? '' : 'excluded'}" data-index="${i}" data-type="silence">
  1061→                <input type="checkbox" class="preview-item-check" ${isSelected ? 'checked' : ''}>
  1062→                <div class="preview-item-info">
