  1068→    });
  1069→
  1070→    // Render takes
  1071→    previewTakes.forEach((take, i) => {
  1072→        // SECURITY: Escape take label to prevent XSS
  1073→        const safeLabel = escapeHtml(take.label || 'Take ' + (take.takeNumber || i + 1));
  1074→        html.push(`
