  1079→                    <div class="preview-item-label">${safeLabel}</div>
  1080→                </div>
  1081→            </div>
  1082→        `);
  1083→    });
  1084→
  1085→    ui.previewList.innerHTML = html.join('');
  1086→
  1087→    // Add click handlers
  1088→    ui.previewList.querySelectorAll('.preview-item').forEach(item => {
  1089→        item.addEventListener('click', (e) => {
  1090→            const index = parseInt(item.dataset.index);
  1091→            const type = item.dataset.type;
  1092→
  1093→            if (e.target.classList.contains('preview-item-check')) {
  1094→                // Checkbox clicked
  1095→                toggleSilenceSelection(index);
  1096→            } else {
  1097→                // Item clicked - seek to time
  1098→                const data = type === 'silence' ? previewSilences[index] : previewTakes[index];
  1099→                seekToTime(data.start);
  1100→            }
  1101→        });
  1102→    });
  1103→}
