  1124→async function seekToTime(seconds) {
  1125→    try {
  1126→        // Check if there's an active sequence before seeking
  1127→        if (!app?.project?.activeSequence) {
  1128→            debugLog('No active sequence to seek in');
  1129→            return;
  1130→        }
  1131→        // Use JSX to set playhead position
  1132→        // Note: setPlayerPosition expects ticks as a string without quotes around the number value
  1133→        const ticks = Math.round(seconds * 254016000000);
  1134→        await jsx.evalScript(`app.project.activeSequence.setPlayerPosition(${ticks})`);
  1135→    } catch (e) {
  1136→        console.warn('[SPLICE] Seek failed:', e);
  1137→    }
  1138→}
