  1143→function renderChapterList() {
  1144→    if (!ui.chapterList) return;
  1145→
  1146→    const html = currentChapters.map((chapter, i) => {
  1147→        // SECURITY: Escape chapter title to prevent XSS
  1148→        const safeTitle = escapeHtml(chapter.title);
  1149→        return `
  1150→            <div class="chapter-item" data-index="${i}">
  1151→                <div class="chapter-time">${formatTime(chapter.startTime)}</div>
  1152→                <div class="chapter-title">${safeTitle}</div>
