  1253→async function buildSequence() {
  1254→    if (isOperationInProgress) return;
  1255→
  1256→    isOperationInProgress = true;
  1257→    setStatus('Building sequence...');
  1258→
  1259→    try {
  1260→        const settings = getSettings();
  1261→
  1262→        // Ensure we have sequence info
  1263→        if (!currentSequenceInfo) {
  1264→            currentSequenceInfo = await jsx.call('getActiveSequence');
  1265→        }
  1266→
  1267→        // Create cut list from selected silences
  1268→        const selectedSilences = previewSilences.filter((_, i) => selectedSilenceIndices.has(i));
  1269→
  1270→        // Calculate total duration from sequence info or last silence
  1271→        let duration = 0;
  1272→        if (currentSequenceInfo && currentSequenceInfo.duration) {
  1273→            duration = currentSequenceInfo.duration;
  1274→        } else if (previewSilences.length > 0) {
  1275→            const lastSilence = previewSilences[previewSilences.length - 1];
  1276→            duration = lastSilence.end + 10; // Add buffer
  1277→        }
  1278→
  1279→        // Call backend to generate cut list
  1280→        const cutListResponse = await fetchWithTimeout(
  1281→            `${getBackendUrl()}/cut-list`,
  1282→            {
  1283→                method: 'POST',
  1284→                headers: getAuthHeaders(),
  1285→                body: JSON.stringify({
  1286→                    sourceName: currentSequenceInfo?.name || 'Untitled Sequence',
  1287→                    sourcePath: currentSequenceInfo?.treePath || currentSequenceInfo?.name || 'Untitled',
  1288→                    duration: duration,
  1289→                    silences: selectedSilences,
  1290→                    takes: previewTakes,
  1291→                    enableTakes: settings.enableTakesDetection,
  1292→                    jCutOffset: settings.enableJCut ? settings.jcutLeadIn : 0,
  1293→                    lCutOffset: settings.enableJCut ? settings.jcutLeadOut : 0
  1294→                })
  1295→            }
  1296→        );
  1297→
  1298→        if (!cutListResponse.ok) {
  1299→            throw new Error(await parseErrorResponse(cutListResponse));
  1300→        }
  1301→
  1302→        const cutList = await cutListResponse.json();
  1303→
  1304→        // Build sequence via JSX
  1305→        const result = await jsx.call('buildSequenceFromCutList', JSON.stringify(cutList));
  1306→
  1307→        if (result && result.success) {
  1308→            setStatus(`Built sequence: ${result.sequenceName}`);
  1309→            hidePreview();
  1310→        } else {
  1311→            throw new Error(result?.error || 'Build failed');
  1312→        }
  1313→    } catch (error) {
  1314→        setStatus('Build failed: ' + error.message, true);
  1315→    } finally {
  1316→        isOperationInProgress = false;
  1317→    }
  1318→}
