  1471→/**
  1472→ * ACCESSIBILITY: Announce message to screen readers via ARIA live region
  1473→ * @param {string} message - Message to announce
  1474→ * @param {string} priority - 'polite' or 'assertive'
  1475→ */
  1476→function announceToScreenReader(message, priority = 'polite') {
  1477→    // Use existing status element or create temporary one
  1478→    const statusEl = document.getElementById('status');
  1479→    if (statusEl && priority === 'polite') {
  1480→        const originalText = statusEl.textContent;
  1481→        statusEl.textContent = message;
  1482→        // Restore after announcement
  1483→        setTimeout(() => {
  1484→            if (statusEl.textContent === message) {
  1485→                statusEl.textContent = originalText;
  1486→            }
  1487→        }, 1000);
  1488→        return;
  1489→    }
  1490→
  1491→    // Create temporary live region for assertive announcements
  1492→    const liveRegion = document.createElement('div');
  1493→    liveRegion.setAttribute('role', 'alert');
  1494→    liveRegion.setAttribute('aria-live', priority);
  1495→    liveRegion.setAttribute('aria-atomic', 'true');
  1496→    liveRegion.className = 'sr-only';
  1497→    liveRegion.style.cssText = 'position:absolute;width:1px;height:1px;padding:0;margin:-1px;overflow:hidden;clip:rect(0,0,0,0);border:0;';
  1498→    liveRegion.textContent = message;
  1499→    document.body.appendChild(liveRegion);
  1500→
  1501→    // Remove after announcement
  1502→    setTimeout(() => liveRegion.remove(), 1000);
  1503→}
