   201→    enableTakesDetection: true,
   202→    enableJCut: false,
   203→    jcutLeadIn: 0.3,
   204→    jcutLeadOut: 0.2,
   205→    enableZoom: false,
   206→    zoomFrequency: 'medium',
   207→    zoomPreset: 'medium',
   208→    zoomPlacement: 'sentence_start',
   209→    enableChapters: false,
