   656→/**
   657→ * Validate license key format: SPLICE-XXXX-XXXX-XXXX
   658→ * Character set excludes confusing chars: 0/O, 1/I/L
   659→ */
   660→function isValidLicenseKeyFormat(key) {
   661→    if (!key || typeof key !== 'string') return false;
   662→    const normalized = key.toUpperCase().trim();
   663→    const pattern = /^SPLICE-[ABCDEFGHJKMNPQRSTUVWXYZ23456789]{4}-[ABCDEFGHJKMNPQRSTUVWXYZ23456789]{4}-[ABCDEFGHJKMNPQRSTUVWXYZ23456789]{4}$/;
   664→    return pattern.test(normalized);
   665→}
   666→
   667→/**
   668→ * Initialize login modal handlers
   669→ */
   670→function initLoginModal() {
   671→    const loginModal = document.getElementById('loginModal');
   672→    const closeLoginBtn = document.getElementById('closeLoginBtn');
   673→    const saveLoginBtn = document.getElementById('saveLoginBtn');
   674→    const licenseKeyInput = document.getElementById('licenseKeyInput');
   675→    const creditBadge = document.getElementById('creditBadge');
   676→
   677→    // Handle credit badge click - retry on error, or show login modal
   678→    if (creditBadge) {
   679→        creditBadge.addEventListener('click', async () => {
   680→            if (creditBadge.classList.contains('error')) {
   681→                creditBadge.textContent = '...';
   682→                if (typeof refreshCredits === 'function') {
   683→                    await refreshCredits();
   684→                }
   685→                return;
   686→            }
   687→            showLoginModal();
   688→        });
   689→    }
   690→
   691→    // Close login modal
