   429→
   430→    // Save merged settings
   431→    saveSettings({
   432→        activePreset: presetId,
   433→        sensitivity: settings.sensitivity,
   434→        threshold: settings.threshold,
   435→        minSilenceLength: settings.minSilenceLength,
   436→        paddingStart: settings.paddingStart,
