  1088→            saveSettings({ rememberOptions: rememberOptions.checked });
  1089→            if (rememberOptions.checked) {
  1090→                setStatus('Options will be remembered');
  1091→            } else {
  1092→                setStatus('Options will reset to defaults');
  1093→            }
  1094→        });
  1095→    }
  1096→
  1097→    // Login modal
  1098→    if (ui.creditBadge) {
  1099→        ui.creditBadge.addEventListener('click', () => {
  1100→            const settings = getSettings();
  1101→            if (!settings.customerId) {
  1102→                showLoginModal();
  1103→            }
  1104→        });
  1105→    }
  1106→
  1107→    if (ui.closeLoginBtn && ui.loginModal) {
  1108→        ui.closeLoginBtn.addEventListener('click', () => {
  1109→            ui.loginModal.classList.add('hidden');
  1110→        });
  1111→    }
  1112→
  1113→    if (ui.saveLoginBtn) {
  1114→        ui.saveLoginBtn.addEventListener('click', activateLicense);
  1115→    }
  1116→
  1117→    // License lookup button
  1118→    const lookupLicenseBtn = document.getElementById('lookupLicenseBtn');
  1119→    if (lookupLicenseBtn) {
  1120→        lookupLicenseBtn.addEventListener('click', lookupLicense);
  1121→    }
  1122→
  1123→    // Close modals on backdrop click
  1124→    document.querySelectorAll('.modal').forEach(modal => {
  1125→        modal.addEventListener('click', (e) => {
  1126→            if (e.target === modal) {
  1127→                modal.classList.add('hidden');
  1128→            }
  1129→        });
  1130→    });
  1131→}
  1132→
  1133→function showLoginModal() {
  1134→    if (ui.loginModal) ui.loginModal.classList.remove('hidden');
  1135→}
  1136→
  1137→async function activateLicense() {
  1138→    if (!ui.licenseKeyInput) return;
  1139→
  1140→    const licenseKey = ui.licenseKeyInput.value.trim();
  1141→    if (!licenseKey) {
  1142→        setStatus('Please enter a license key', true);
  1143→        return;
  1144→    }
  1145→
  1146→    try {
  1147→        const response = await fetchWithTimeout(
  1148→            `${getBackendUrl()}/license/activate`,
  1149→            {
  1150→                method: 'POST',
  1151→                headers: { 'Content-Type': 'application/json' },
  1152→                body: JSON.stringify({ key: licenseKey })
  1153→            }
  1154→        );
  1155→
  1156→        if (!response.ok) {
  1157→            throw new Error(await parseErrorResponse(response));
  1158→        }
  1159→
  1160→        const data = await response.json();
  1161→        saveSettings({
  1162→            customerId: data.customerId,
  1163→            licenseKey: licenseKey
  1164→        });
  1165→
  1166→        if (ui.loginModal) ui.loginModal.classList.add('hidden');
  1167→        setStatus('License activated');
  1168→        updateCredits();
  1169→    } catch (error) {
  1170→        setStatus('Activation failed: ' + error.message, true);
  1171→    }
  1172→}
  1173→
  1174→/**
  1175→ * Look up license key by email address
  1176→ */
  1177→async function lookupLicense() {
  1178→    const lookupEmailInput = document.getElementById('lookupEmailInput');
  1179→    const licenseKeyInput = document.getElementById('licenseKeyInput');
  1180→
  1181→    if (!lookupEmailInput) return;
  1182→
  1183→    const email = lookupEmailInput.value.trim().toLowerCase();
  1184→    if (!email) {
  1185→        setStatus('Please enter your email address', true);
  1186→        return;
  1187→    }
  1188→
  1189→    // Basic email validation
  1190→    const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
  1191→    if (!emailRegex.test(email)) {
  1192→        setStatus('Please enter a valid email address', true);
  1193→        return;
  1194→    }
  1195→
  1196→    const lookupBtn = document.getElementById('lookupLicenseBtn');
  1197→    if (lookupBtn) {
  1198→        lookupBtn.disabled = true;
  1199→        lookupBtn.textContent = 'Looking up...';
  1200→    }
  1201→
  1202→    try {
  1203→        const response = await fetchWithTimeout(
  1204→            `${getBackendUrl()}/license/lookup`,
  1205→            {
  1206→                method: 'POST',
  1207→                headers: { 'Content-Type': 'application/json' },
  1208→                body: JSON.stringify({ email })
  1209→            },
  1210→            30000
  1211→        );
  1212→
  1213→        const result = await response.json();
  1214→
  1215→        // Note: For security, the backend does NOT return the license key directly.
  1216→        // It sends the key via email to prevent enumeration attacks.
  1217→        if (result.success) {
  1218→            setStatus(result.message || 'If a license exists, it has been sent to your email.');
  1219→        } else {
  1220→            setStatus(result.error || 'Lookup failed. Please try again.', true);
  1221→        }
  1222→    } catch (error) {
  1223→        setStatus('Lookup failed: ' + error.message, true);
  1224→    } finally {
  1225→        if (lookupBtn) {
  1226→            lookupBtn.disabled = false;
  1227→            lookupBtn.textContent = 'Lookup';
  1228→        }
  1229→    }
  1230→}
  1231→
  1232→// ============================================================================
  1233→// CREDITS
  1234→// ============================================================================
  1235→function initCredits() {
  1236→    updateCredits();
  1237→}
  1238→
  1239→async function updateCredits() {
  1240→    if (!ui.creditBadge) return;
  1241→
  1242→    const settings = getSettings();
  1243→    if (!settings.customerId) {
  1244→        ui.creditBadge.className = 'credit-badge login';
  1245→        ui.creditBadge.textContent = 'Login';
  1246→        ui.creditBadge.style.display = 'flex';
  1247→        return;
  1248→    }
  1249→
  1250→    try {
  1251→        const response = await fetchWithTimeout(
  1252→            `${getBackendUrl()}/credits`,
  1253→            {
  1254→                method: 'GET',
  1255→                headers: getAuthHeaders()
  1256→            }
  1257→        );
  1258→
  1259→        if (response.ok) {
  1260→            const data = await response.json();
  1261→            // Backend returns hoursRemaining (not remainingMinutes)
  1262→            const hours = (data.hoursRemaining || 0).toFixed(1);
  1263→            ui.creditBadge.textContent = `${hours}h`;
  1264→            // Low warning when less than 1 hour remaining
  1265→            ui.creditBadge.className = data.hoursRemaining < 1 ? 'credit-badge low' : 'credit-badge ok';
  1266→            ui.creditBadge.style.display = 'flex';
  1267→        } else {
  1268→            ui.creditBadge.className = 'credit-badge error';
  1269→            ui.creditBadge.textContent = 'Error';
  1270→            ui.creditBadge.style.display = 'flex';
  1271→        }
  1272→    } catch {
  1273→        ui.creditBadge.className = 'credit-badge error';
  1274→        ui.creditBadge.textContent = 'Offline';
  1275→        ui.creditBadge.style.display = 'flex';
  1276→    }
  1277→}
  1278→
  1279→// ============================================================================
  1280→// HELP BUTTON
  1281→// ============================================================================
  1282→function initHelpButton() {
  1283→    const helpBtn = document.getElementById('helpBtn');
  1284→    if (helpBtn) {
  1285→        helpBtn.addEventListener('click', () => {
  1286→            setStatus('Silence: removes quiet gaps | Takes: detects repeated content | Chapters: AI topic segmentation');
  1287→        });
  1288→    }
  1289→}
  1290→
  1291→// Export for debugging
  1292→window.spliceDebug = {
  1293→    getState: () => ({
  1294→        silences: previewSilences,
  1295→        takes: previewTakes,
  1296→        chapters: currentChapters,
