    80→/**
    81→ * Generate a slugified ID from a preset name
    82→ * @param {string} name - Preset name
    83→ * @returns {string} Slugified ID
    84→ */
    85→function slugifyPresetName(name) {
    86→  return name
    87→    .toLowerCase()
    88→    .trim()
    89→    .replace(/[^a-z0-9]+/g, '-')
    90→    .replace(/^-+|-+$/g, '')
    91→    .substring(0, 50) || 'preset';
    92→}
