   655→
   656→  // Build combined items list (sorted by time)
   657→  const items = [
   658→    ...safeToRemove.map((s, i) => ({ type: 'silence', index: i, start: s.start, end: s.end, data: s })),
   659→    ...protectedSilences.map(s => ({ type: 'protected', start: s.start, end: s.end, data: s })),
   660→    ...takes.map(t => ({ type: 'take', start: t.startTime || t.start, end: t.endTime || t.end, data: t }))
