   682→    } else if (item.type === 'protected') {
   683→      // Protected by take - yellow marker, no checkbox
   684→      div.className = 'preview-item protected-silence';
   685→      div.innerHTML = `
   686→        <div style="width: 20px; margin-right: 10px;"></div>
   687→        <div class="preview-item-info">
   688→          <div class="preview-item-time">${formatTime(item.start)} - ${formatTime(item.end)}</div>
   689→          <div class="preview-item-duration">${duration.toFixed(2)}s (overlaps speech)</div>
   690→        </div>
   691→        <button class="preview-item-seek" data-time="${item.start}" title="Seek">&gt;</button>
   692→      `;
   693→    } else if (item.type === 'take') {
   694→      // Take (speech segment) - blue marker, no checkbox
   695→      div.className = 'preview-item take-marker';
   696→      const textPreview = item.data.text?.substring(0, 40) || 'Speech segment';
   697→      div.innerHTML = `
   698→        <div style="width: 20px; margin-right: 10px;"></div>
   699→        <div class="preview-item-info">
   700→          <div class="preview-item-time">${formatTime(item.start)} - ${formatTime(item.end)}</div>
   701→          <div class="preview-item-duration">${textPreview}${item.data.text?.length > 40 ? '...' : ''}</div>
   702→        </div>
   703→        <button class="preview-item-seek" data-time="${item.start}" title="Seek to take">&gt;</button>
   704→      `;
   705→    }
   706→
   707→    fragment.appendChild(div);
   708→  });
   709→
   710→  if (ui.previewList) {
   711→    ui.previewList.textContent = '';
   712→    ui.previewList.appendChild(fragment);
