   929→function selectByDuration(minSec, maxSec) {
   930→  selectedSilenceIndices.clear();
   931→
   932→  safeSilences.forEach((silence, index) => {
   933→    const duration = silence.end - silence.start;
   934→    if (duration >= minSec && duration < maxSec) {
