   946→      item?.classList.remove('excluded');
   947→    } else {
   948→      item?.classList.add('excluded');
   949→    }
   950→  });
   951→
   952→  updateSelectionCounts();
   953→  setStatus(`Selected ${selectedSilenceIndices.size} silences (${minSec}s - ${maxSec}s)`);
