   978→async function applySelectedSilences() {
   979→  if (selectedSilenceIndices.size === 0) {
   980→    setStatus('No silences selected');
   981→    return;
   982→  }
   983→
   984→  showProgress('Reading marker positions...');
   985→
   986→  // Read current marker positions from timeline
   987→  // This allows users to manually adjust markers before applying
   988→  const markerSilences = await readMarkersAssilences();
   989→
   990→  let silencesToApply;
   991→  let adjustedCount = 0;
   992→
   993→  if (markerSilences.length > 0) {
   994→    // Use marker positions (may have been adjusted by user)
   995→    silencesToApply = markerSilences;
