  1113→  // Map technical errors to user-friendly messages
  1114→  const errorMap = {
  1115→    'Builder not initialized': 'Builder module failed to load. Try restarting the plugin.',
  1116→    'No active project': 'No project is open. Please open or create a project first.',
  1117→    'No media items found': 'No media files in project. Import media before building.',
