  1592→  const batchEmpty = document.getElementById('batchEmpty');
  1593→  const batchList = document.getElementById('batchList');
  1594→  const startBatchBtn = document.getElementById('startBatchBtn');
  1595→
  1596→  if (batchQueue.length === 0) {
