   277→    }
   278→  });
   279→});
   280→
   281→/**
   282→ * GET /health - Health check
   283→ */
   284→app.get('/health', (req, res) => {
   285→  res.json({ status: 'ok', service: 'splice-backend' });
   286→});
   287→
   288→/**
   289→ * POST /analyze - Main analysis endpoint
   290→ *
   291→ * Pipeline:
   292→ * 1. Validate input (wavPath)
   293→ * 2. Slice 4: Transcribe audio with Groq Whisper
   294→ * 3. Slice 5: Detect takes with GPT-4o-mini
   295→ * 4. Return combined results
   296→ */
   297→app.post('/analyze', requireCredits({ endpoint: 'analyze' }), async (req, res) => {
   298→  const { wavPath } = req.body;
   299→
   300→  // Validate input
   301→  if (!wavPath) {
   302→    return res.status(400).json({ error: 'wavPath is required' });
   303→  }
   304→
   305→  if (!fs.existsSync(wavPath)) {
   306→    return res.status(404).json({ error: `File not found: ${wavPath}` });
   307→  }
   308→
   309→  console.log(`[SPLICE] Analyzing: ${wavPath}`);
   310→
   311→  try {
   312→    // Slice 4 - GPT-4o-mini transcription
   313→    const transcript = await transcribeAudio(wavPath);
   314→
   315→    // Slice 5 - GPT-4o-mini take detection
   316→    const takes = await detectTakes(transcript);
   317→
   318→    // Deduct usage based on audio duration
   319→    const audioDuration = transcript.duration || 0;
   320→    let balance = null;
   321→    if (audioDuration > 0 && req.deductUsage) {
   322→      balance = await req.deductUsage(audioDuration);
   323→    }
   324→
   325→    res.json({
   326→      success: true,
   327→      wavPath,
   328→      transcript,
   329→      takes,
   330→      balance: balance ? { hoursRemaining: balance.hoursRemaining, tier: balance.tier } : undefined
   331→    });
   332→  } catch (err) {
   333→    console.error('[SPLICE] Error:', err);
   334→    res.status(500).json({ error: err.message });
   335→  }
   336→});
   337→
   338→/**
   339→ * POST /silences - Detect silent gaps in audio
   340→ *
   341→ * Pipeline:
   342→ * 1. Transcribe audio with Whisper (reuses transcription)
   343→ * 2. Analyze gaps between segments
   344→ * 3. Return silence regions
   345→ */
   346→app.post('/silences', requireCredits({ endpoint: 'silences' }), async (req, res) => {
   347→  const { wavPath, threshold = 0.5 } = req.body;
   348→
   349→  if (!wavPath) {
   350→    return res.status(400).json({ error: 'wavPath is required' });
   351→  }
   352→
   353→  if (!fs.existsSync(wavPath)) {
   354→    return res.status(404).json({ error: `File not found: ${wavPath}` });
   355→  }
   356→
   357→  console.log(`[SPLICE] Detecting silences: ${wavPath} (threshold: ${threshold}s)`);
   358→
   359→  try {
   360→    const transcript = await transcribeAudio(wavPath);
   361→    const silences = detectSilences(transcript.segments, threshold);
   362→
   363→    // Deduct usage based on audio duration
   364→    const audioDuration = transcript.duration || 0;
   365→    let balance = null;
   366→    if (audioDuration > 0 && req.deductUsage) {
   367→      balance = await req.deductUsage(audioDuration);
   368→    }
   369→
   370→    res.json({
   371→      success: true,
   372→      wavPath,
   373→      threshold,
   374→      silences,
   375→      count: silences.length,
   376→      totalSilenceDuration: silences.reduce((sum, s) => sum + s.duration, 0).toFixed(2),
   377→      balance: balance ? { hoursRemaining: balance.hoursRemaining, tier: balance.tier } : undefined
   378→    });
   379→  } catch (err) {
   380→    console.error('[SPLICE] Silence detection error:', err);
   381→    res.status(500).json({ error: err.message });
   382→  }
   383→});
   384→
   385→/**
   386→ * POST /silences-audio - Detect silences using FFprobe audio analysis
   387→ *
   388→ * Uses actual audio levels (dB threshold) instead of transcript gaps.
   389→ * More accurate for detecting silence vs background noise.
   390→ */
   391→app.post('/silences-audio', requireCredits({ endpoint: 'silences-audio' }), async (req, res) => {
   392→  const {
   393→    wavPath,
   394→    threshold = -30,
   395→    minDuration = 0.5,
   396→    padding = 0.1
   397→  } = req.body;
   398→
   399→  if (!wavPath) {
   400→    return res.status(400).json({ error: 'wavPath is required' });
   401→  }
   402→
   403→  if (!fs.existsSync(wavPath)) {
   404→    return res.status(404).json({ error: `File not found: ${wavPath}` });
   405→  }
   406→
   407→  // Check FFprobe availability
   408→  const ffprobeAvailable = await isFFprobeInstalled();
   409→  if (!ffprobeAvailable) {
   410→    return res.status(500).json({
   411→      error: 'FFprobe not installed. Run: brew install ffmpeg'
   412→    });
   413→  }
   414→
   415→  console.log(`[SPLICE] FFprobe silence detection: ${wavPath} (threshold: ${threshold}dB, min: ${minDuration}s)`);
   416→
   417→  try {
   418→    const silences = await detectAudioSilences(wavPath, {
   419→      threshold,
   420→      minDuration,
   421→      padding
   422→    });
   423→
   424→    const totalDuration = silences.reduce((sum, s) => sum + s.duration, 0);
   425→
   426→    // Deduct usage based on audio duration
   427→    let balance = null;
   428→    try {
   429→      const audioDuration = await getAudioDuration(wavPath);
   430→      if (audioDuration > 0 && req.deductUsage) {
   431→        balance = await req.deductUsage(audioDuration);
   432→      }
   433→    } catch (durErr) {
   434→      console.warn('[SPLICE] Could not get audio duration for billing:', durErr.message);
   435→    }
   436→
   437→    res.json({
   438→      success: true,
   439→      wavPath,
   440→      threshold,
   441→      minDuration,
   442→      padding,
   443→      silences,
   444→      count: silences.length,
   445→      totalSilenceDuration: totalDuration.toFixed(2),
   446→      balance: balance ? { hoursRemaining: balance.hoursRemaining, tier: balance.tier } : undefined
   447→    });
   448→  } catch (err) {
   449→    console.error('[SPLICE] FFprobe silence detection error:', err);
   450→    res.status(500).json({ error: err.message });
   451→  }
   452→});
   453→
   454→/**
   455→ * POST /silences-rms - Detect silences using RMS audio analysis
   456→ *
   457→ * Advanced silence detection with:
   458→ * - RMS (Root Mean Square) audio level analysis
   459→ * - Auto-threshold detection from audio histogram
   460→ * - Configurable padding (before/after cuts)
   461→ * - Sensitivity slider mapping (0-100)
   462→ *
   463→ * Options:
   464→ * - threshold: dBFS threshold (-60 to -20, default: -30)
   465→ * - minSilenceLength: Minimum silence duration in seconds (default: 0.5)
   466→ * - seekStep: Analysis window step in seconds (default: 0.05)
   467→ * - paddingStart: Buffer before silence in seconds (default: 0.1)
   468→ * - paddingEnd: Buffer after silence in seconds (default: 0.05)
   469→ * - autoThreshold: Auto-detect optimal threshold (default: false)
   470→ * - sensitivity: UI sensitivity 0-100 (overrides other params if provided)
   471→ */
   472→app.post('/silences-rms', requireCredits({ endpoint: 'silences-rms' }), async (req, res) => {
   473→  const { wavPath, sensitivity, ...manualOptions } = req.body;
   474→
   475→  if (!wavPath) {
   476→    return res.status(400).json({ error: 'wavPath is required' });
   477→  }
   478→
   479→  if (!fs.existsSync(wavPath)) {
   480→    return res.status(404).json({ error: `File not found: ${wavPath}` });
   481→  }
   482→
   483→  // Validate file size to prevent OOM
   484→  const sizeCheck = await validateFileSize(wavPath);
   485→  if (!sizeCheck.valid) {
   486→    return res.status(413).json({ error: sizeCheck.error });
   487→  }
   488→
   489→  // Check FFprobe availability (needed for audio extraction)
   490→  const ffprobeAvailable = await isFFprobeInstalled();
   491→  if (!ffprobeAvailable) {
   492→    return res.status(500).json({
   493→      error: 'FFprobe not installed. Run: brew install ffmpeg'
   494→    });
   495→  }
   496→
   497→  // Build options - use sensitivity if provided, otherwise use manual options
   498→  let options = {};
   499→  if (typeof sensitivity === 'number') {
   500→    options = sensitivityToParams(sensitivity);
   501→    console.log(`[SPLICE] RMS detection with sensitivity ${sensitivity}`);
   502→  } else {
   503→    options = {
   504→      threshold: manualOptions.threshold ?? -30,
   505→      minSilenceLength: manualOptions.minSilenceLength ?? 0.5,
   506→      seekStep: manualOptions.seekStep ?? 0.05,
   507→      paddingStart: manualOptions.paddingStart ?? 0.1,
   508→      paddingEnd: manualOptions.paddingEnd ?? 0.05,
   509→      autoThreshold: manualOptions.autoThreshold ?? false,
   510→      mergeDistance: manualOptions.mergeDistance ?? 0.2
   511→    };
   512→  }
   513→
   514→  console.log(`[SPLICE] RMS silence detection: ${wavPath}`);
   515→
   516→  try {
   517→    const result = await detectSilencesRMS(wavPath, options);
   518→
   519→    // Deduct usage based on audio duration
   520→    const audioDuration = result.metadata?.audioDuration || 0;
   521→    let balance = null;
   522→    if (audioDuration > 0 && req.deductUsage) {
   523→      balance = await req.deductUsage(audioDuration);
   524→    }
   525→
   526→    res.json({
   527→      success: true,
   528→      wavPath,
   529→      ...result,
   530→      count: result.silences.length,
   531→      totalSilenceDuration: result.metadata.totalSilenceDuration.toFixed(2),
   532→      balance: balance ? { hoursRemaining: balance.hoursRemaining, tier: balance.tier } : undefined
   533→    });
   534→  } catch (err) {
   535→    console.error('[SPLICE] RMS silence detection error:', err);
   536→    res.status(500).json({ error: err.message });
   537→  }
   538→});
   539→
   540→// =============================================================================
   541→// Profanity Detection Routes
   542→// =============================================================================
   543→
   544→/**
   545→ * POST /profanity - Detect profanity in audio/transcript
   546→ *
   547→ * Transcribes audio (if needed) and detects profanity words.
   548→ * Returns word-level and segment-level results for muting/bleeping.
   549→ *
   550→ * Options:
   551→ * - wavPath: Path to audio file (required)
   552→ * - transcript: Pre-existing transcript (optional, skips transcription)
   553→ * - language: Language code (en, es, fr, de) - default: en
   554→ * - customBlocklist: Array or comma-separated string of additional words to censor
   555→ * - customAllowlist: Array or comma-separated string of words to allow
   556→ * - frameRate: Frame rate for boundary alignment (default: 30)
   557→ */
   558→app.post('/profanity', requireCredits({ endpoint: 'profanity' }), async (req, res) => {
   559→  const {
   560→    wavPath,
