   750→/**
   751→ * POST /fillers - Detect filler words (um, uh, like, etc.)
   752→ *
   753→ * Transcribes audio and identifies filler words with timestamps.
   754→ * Returns segments that can be cut or reviewed for removal.
   755→ *
   756→ * Options:
   757→ * - wavPath: Path to audio file (required unless transcript provided)
   758→ * - transcript: Pre-existing transcript with word-level timing (optional)
   759→ * - customFillers: Additional filler words to detect (optional)
   760→ */
   761→app.post('/fillers', requireCredits({ endpoint: 'fillers' }), async (req, res) => {
   762→  const {
   763→    wavPath,
   764→    transcript: providedTranscript,
   765→    customFillers = []
   766→  } = req.body;
   767→
   768→  if (!wavPath && !providedTranscript) {
   769→    return res.status(400).json({ error: 'wavPath or transcript is required' });
   770→  }
   771→
   772→  if (wavPath && !fs.existsSync(wavPath)) {
   773→    return res.status(404).json({ error: `File not found: ${wavPath}` });
   774→  }
   775→
   776→  console.log(`[SPLICE] Filler word detection: ${wavPath || 'provided transcript'}`);
   777→
   778→  try {
   779→    // Get or create transcript with word-level timestamps
   780→    let transcript = providedTranscript;
   781→    if (!transcript && wavPath) {
   782→      transcript = await transcribeWithWords(wavPath);
   783→    }
   784→
   785→    // Validate transcript has words
   786→    if (!transcript || !transcript.words || transcript.words.length === 0) {
   787→      return res.status(400).json({
   788→        error: 'Transcript must contain word-level timing data'
   789→      });
   790→    }
   791→
   792→    // Default filler words (common in English speech)
   793→    const defaultFillers = [
   794→      'um', 'uh', 'ah', 'er', 'eh',           // Hesitation sounds
   795→      'like', 'so', 'well', 'right',           // Discourse markers
   796→      'you know', 'i mean', 'basically',       // Filler phrases
   797→      'actually', 'literally', 'honestly',     // Overused qualifiers
   798→      'kind of', 'sort of', 'you see'          // Hedging phrases
   799→    ];
   800→
   801→    // Combine default + custom fillers (lowercase for matching)
   802→    const fillerSet = new Set([
   803→      ...defaultFillers,
   804→      ...customFillers.map(f => f.toLowerCase().trim())
   805→    ]);
   806→
   807→    // Detect filler words
   808→    const fillers = [];
   809→    const words = transcript.words;
   810→
   811→    for (let i = 0; i < words.length; i++) {
   812→      const word = words[i];
   813→      const normalizedWord = word.word.toLowerCase().replace(/[.,!?;:'"]/g, '').trim();
   814→
   815→      // Check single-word fillers
   816→      if (fillerSet.has(normalizedWord)) {
   817→        fillers.push({
   818→          word: word.word,
   819→          normalizedWord,
   820→          start: word.start,
   821→          end: word.end,
   822→          duration: word.end - word.start,
   823→          index: i,
   824→          type: 'filler'
   825→        });
   826→        continue;
   827→      }
   828→
   829→      // Check two-word phrases (e.g., "you know", "kind of")
   830→      if (i < words.length - 1) {
   831→        const nextWord = words[i + 1];
   832→        const twoWordPhrase = `${normalizedWord} ${nextWord.word.toLowerCase().replace(/[.,!?;:'"]/g, '').trim()}`;
   833→        if (fillerSet.has(twoWordPhrase)) {
   834→          fillers.push({
   835→            word: `${word.word} ${nextWord.word}`,
   836→            normalizedWord: twoWordPhrase,
   837→            start: word.start,
   838→            end: nextWord.end,
   839→            duration: nextWord.end - word.start,
   840→            index: i,
   841→            type: 'filler_phrase'
   842→          });
   843→          // Skip next word since it's part of this phrase
   844→          i++;
   845→        }
   846→      }
   847→    }
   848→
   849→    // Calculate total filler time
   850→    const totalFillerDuration = fillers.reduce((sum, f) => sum + f.duration, 0);
   851→    const audioDuration = transcript.duration || (words.length > 0 ? words[words.length - 1].end : 0);
   852→    const fillerPercentage = audioDuration > 0 ? (totalFillerDuration / audioDuration) * 100 : 0;
   853→
   854→    // Deduct usage based on audio duration
   855→    let balance = null;
   856→    if (audioDuration > 0 && req.deductUsage) {
   857→      balance = await req.deductUsage(audioDuration);
   858→    }
   859→
   860→    res.json({
   861→      success: true,
   862→      wavPath,
   863→      fillers,
   864→      metadata: {
   865→        totalWords: words.length,
   866→        fillerCount: fillers.length,
   867→        totalFillerDuration: parseFloat(totalFillerDuration.toFixed(3)),
   868→        audioDuration: parseFloat(audioDuration.toFixed(3)),
   869→        fillerPercentage: parseFloat(fillerPercentage.toFixed(2)),
   870→        fillersPerMinute: audioDuration > 0 ? parseFloat((fillers.length / (audioDuration / 60)).toFixed(2)) : 0
   871→      },
   872→      removalSegments: fillers.map(f => ({
   873→        start: f.start,
   874→        end: f.end,
   875→        duration: f.duration,
   876→        reason: `Filler: "${f.word}"`,
   877→        type: f.type
   878→      })),
   879→      balance: balance ? { hoursRemaining: balance.hoursRemaining, tier: balance.tier } : undefined
   880→    });
   881→  } catch (err) {
   882→    console.error('[SPLICE] Filler detection error:', err);
   883→    res.status(500).json({ error: err.message });
   884→  }
   885→});
