  1115→    wideShotPercentage = 20,
  1116→    wideShotTracks = [],
  1117→    cutawayEnabled = false,
  1118→    cutawayTracks = [],
  1119→    speakerBoosts = {},
  1120→    frameRate = 30
  1121→  } = req.body;
  1122→
  1123→  // Validate audioPaths
  1124→  if (!audioPaths || !Array.isArray(audioPaths) || audioPaths.length === 0) {
  1125→    return res.status(400).json({ error: 'audioPaths array is required (at least 1 path)' });
  1126→  }
  1127→
  1128→  // Validate all files exist
  1129→  for (const audioPath of audioPaths) {
  1130→    if (!fs.existsSync(audioPath)) {
  1131→      return res.status(404).json({ error: `File not found: ${audioPath}` });
  1132→    }
  1133→  }
  1134→
  1135→  // Check FFprobe availability
  1136→  const ffprobeAvailable = await isFFprobeInstalled();
  1137→  if (!ffprobeAvailable) {
  1138→    return res.status(500).json({
  1139→      error: 'FFprobe not installed. Run: brew install ffmpeg'
  1140→    });
  1141→  }
  1142→
  1143→  console.log(`[SPLICE] Multitrack analysis: ${audioPaths.length} track(s)`);
  1144→
  1145→  try {
  1146→    const result = await analyzeMultitrack(audioPaths, {
  1147→      speakerNames: speakerNames || audioPaths.map((_, i) => `Speaker ${i + 1}`),
  1148→      videoTrackMapping,
  1149→      minShotDuration,
  1150→      switchingFrequency,
  1151→      wideShotEnabled,
