  1529→// =============================================================================
  1530→// Batch Processing Routes
  1531→// =============================================================================
  1532→
  1533→// In-memory job queue for batch processing
  1534→const batchJobs = new Map();
  1535→
  1536→// Batch job limits to prevent memory leak
  1537→const MAX_BATCH_JOBS = 10000;
  1538→const BATCH_JOB_MAX_AGE_MS = 24 * 60 * 60 * 1000; // 24 hours
  1539→
  1540→/**
  1541→ * Generate a unique job ID
  1542→ */
  1543→function generateJobId() {
  1544→  return `batch_${Date.now()}_${Math.random().toString(36).substr(2, 9)}`;
  1545→}
  1546→
  1547→/**
  1548→ * Clean up old batch jobs to prevent memory leak
  1549→ * Removes jobs older than 24 hours
  1550→ */
  1551→function cleanupOldBatchJobs() {
  1552→  const now = Date.now();
  1553→  let removedCount = 0;
  1554→
  1555→  for (const [jobId, job] of batchJobs.entries()) {
  1556→    const createdAt = new Date(job.createdAt).getTime();
  1557→    if (now - createdAt > BATCH_JOB_MAX_AGE_MS) {
  1558→      batchJobs.delete(jobId);
  1559→      removedCount++;
  1560→    }
  1561→  }
  1562→
  1563→  if (removedCount > 0) {
  1564→    console.log(`[SPLICE] Cleaned up ${removedCount} old batch job(s)`);
  1565→  }
  1566→}
  1567→
  1568→/**
  1569→ * Enforce max job limit by removing oldest completed jobs
  1570→ */
  1571→function enforceJobLimit() {
  1572→  if (batchJobs.size < MAX_BATCH_JOBS) return;
  1573→
  1574→  // Get completed jobs sorted by creation date (oldest first)
  1575→  const completedJobs = Array.from(batchJobs.entries())
  1576→    .filter(([_, job]) => job.status !== 'processing')
  1577→    .sort((a, b) => new Date(a[1].createdAt) - new Date(b[1].createdAt));
  1578→
  1579→  // Remove oldest completed jobs until under limit
  1580→  const toRemove = batchJobs.size - MAX_BATCH_JOBS + 1;
  1581→  for (let i = 0; i < Math.min(toRemove, completedJobs.length); i++) {
  1582→    batchJobs.delete(completedJobs[i][0]);
  1583→  }
  1584→
  1585→  console.log(`[SPLICE] Enforced job limit, removed ${Math.min(toRemove, completedJobs.length)} job(s)`);
  1586→}
  1587→
  1588→// Run cleanup every hour
  1589→setInterval(cleanupOldBatchJobs, 60 * 60 * 1000);
  1590→
  1591→/**
  1592→ * POST /batch/silences - Process multiple files for silence detection
  1593→ *
  1594→ * Creates a batch job that processes multiple audio files.
  1595→ * Returns a job ID for tracking progress.
  1596→ *
  1597→ * Body:
  1598→ * - files: Array of file paths to process
  1599→ * - options: Detection options (sensitivity, threshold, etc.)
  1600→ */
  1601→app.post('/batch/silences', requireCredits({ endpoint: 'batch-silences' }), async (req, res) => {
  1602→  const { files, options = {} } = req.body;
  1603→
  1604→  if (!files || !Array.isArray(files) || files.length === 0) {
  1605→    return res.status(400).json({ error: 'files array is required' });
  1606→  }
  1607→
  1608→  // Validate all files exist
  1609→  const missingFiles = files.filter(f => !fs.existsSync(f));
  1610→  if (missingFiles.length > 0) {
  1611→    return res.status(404).json({
  1612→      error: 'Some files not found',
  1613→      missingFiles
  1614→    });
  1615→  }
  1616→
  1617→  // Enforce job limit before creating new job
  1618→  enforceJobLimit();
  1619→
  1620→  const jobId = generateJobId();
  1621→
  1622→  // Initialize job with customer ID for usage tracking
  1623→  const job = {
  1624→    id: jobId,
  1625→    type: 'silences',
  1626→    status: 'processing',
  1627→    createdAt: new Date().toISOString(),
  1628→    stripeCustomerId: req.stripeCustomerId,  // Store for usage deduction
  1629→    files: files.map(f => ({
  1630→      path: f,
  1631→      status: 'pending',
  1632→      result: null,
  1633→      error: null
  1634→    })),
  1635→    options,
  1636→    progress: {
  1637→      total: files.length,
  1638→      completed: 0,
  1639→      failed: 0,
  1640→      percentage: 0
  1641→    },
  1642→    results: [],
  1643→    errors: [],
  1644→    totalUsageDeducted: 0  // Track total seconds deducted
  1645→  };
  1646→
  1647→  batchJobs.set(jobId, job);
  1648→  console.log(`[SPLICE] Batch job ${jobId} created with ${files.length} files`);
  1649→
  1650→  // Start processing in background
  1651→  processBatchJob(jobId);
  1652→
  1653→  res.json({
  1654→    success: true,
  1655→    jobId,
  1656→    message: `Batch job created with ${files.length} files`,
  1657→    statusUrl: `/batch/status/${jobId}`
  1658→  });
  1659→});
  1660→
  1661→/**
  1662→ * Process a batch job (runs in background)
  1663→ */
  1664→async function processBatchJob(jobId) {
  1665→  const job = batchJobs.get(jobId);
  1666→  if (!job) return;
  1667→
  1668→  const { sensitivity, ...manualOptions } = job.options;
  1669→
  1670→  // Build detection options
  1671→  let detectionOptions = {};
  1672→  if (typeof sensitivity === 'number') {
  1673→    detectionOptions = sensitivityToParams(sensitivity);
  1674→  } else {
  1675→    detectionOptions = {
  1676→      threshold: manualOptions.threshold ?? -30,
  1677→      minSilenceLength: manualOptions.minSilenceLength ?? 0.5,
  1678→      paddingStart: manualOptions.paddingStart ?? 0.1,
  1679→      paddingEnd: manualOptions.paddingEnd ?? 0.05,
  1680→      autoThreshold: manualOptions.autoThreshold ?? false
  1681→    };
  1682→  }
  1683→
  1684→  // Process files sequentially to avoid overwhelming the system
  1685→  for (let i = 0; i < job.files.length; i++) {
  1686→    const fileEntry = job.files[i];
  1687→    fileEntry.status = 'processing';
  1688→
  1689→    try {
  1690→      const result = await detectSilencesRMS(fileEntry.path, detectionOptions);
  1691→
  1692→      fileEntry.status = 'completed';
  1693→      fileEntry.result = {
  1694→        silences: result.silences,
  1695→        count: result.silences.length,
  1696→        totalSilenceDuration: result.metadata.totalSilenceDuration,
  1697→        audioDuration: result.metadata.audioDuration
  1698→      };
  1699→
  1700→      job.results.push({
  1701→        file: fileEntry.path,
  1702→        ...fileEntry.result
  1703→      });
  1704→
  1705→      // Deduct usage for this file
  1706→      const audioDuration = result.metadata?.audioDuration || 0;
  1707→      if (audioDuration > 0 && job.stripeCustomerId) {
  1708→        try {
  1709→          await usageTracking.deductUsage(job.stripeCustomerId, audioDuration, 'batch-silences');
  1710→          job.totalUsageDeducted += audioDuration;
  1711→        } catch (usageErr) {
  1712→          console.warn(`[SPLICE] Batch ${jobId}: Usage deduction failed:`, usageErr.message);
  1713→        }
  1714→      }
  1715→
  1716→      job.progress.completed++;
  1717→      console.log(`[SPLICE] Batch ${jobId}: ${i + 1}/${job.files.length} completed`);
  1718→    } catch (err) {
  1719→      fileEntry.status = 'failed';
  1720→      fileEntry.error = err.message;
  1721→
  1722→      job.errors.push({
  1723→        file: fileEntry.path,
  1724→        error: err.message
  1725→      });
  1726→
  1727→      job.progress.failed++;
  1728→      console.error(`[SPLICE] Batch ${jobId}: ${fileEntry.path} failed:`, err.message);
  1729→    }
  1730→
  1731→    // Update progress
  1732→    job.progress.percentage = Math.round(
  1733→      ((job.progress.completed + job.progress.failed) / job.progress.total) * 100
  1734→    );
  1735→  }
  1736→
  1737→  // Mark job as complete
  1738→  job.status = job.progress.failed === job.progress.total ? 'failed' :
  1739→               job.progress.failed > 0 ? 'completed_with_errors' : 'completed';
  1740→  job.completedAt = new Date().toISOString();
  1741→
  1742→  console.log(`[SPLICE] Batch job ${jobId} ${job.status}`);
