   524→// Legacy XML processing endpoint (backwards compatibility)
   525→const { processXMLFile } = require('./services/xmlProcessor');
   526→app.post('/process-xml', requireCredits({ endpoint: 'process-xml' }), async (req, res) => {
   527→  const {
   528→    xmlPath,
   529→    silences,
   530→    removeGaps = true,
   531→    outputPath = null
   532→  } = req.body;
   533→
   534→  if (!xmlPath) {
   535→    return res.status(400).json({ error: 'xmlPath is required' });
   536→  }
   537→
   538→  if (!silences || !Array.isArray(silences)) {
   539→    return res.status(400).json({ error: 'silences array is required' });
   540→  }
   541→
   542→  if (!fs.existsSync(xmlPath)) {
   543→    return res.status(404).json({ error: `XML file not found: ${xmlPath}` });
   544→  }
   545→
   546→  console.log(`[SPLICE] Processing XML: ${xmlPath} with ${silences.length} silence(s)`);
   547→
   548→  try {
   549→    const result = await processXMLFile(xmlPath, silences, {
   550→      outputPath,
   551→      removeGaps
   552→    });
   553→
   554→    res.json({
   555→      success: true,
   556→      inputPath: xmlPath,
   557→      outputPath: result.outputPath,
   558→      stats: result.stats
   559→    });
   560→  } catch (err) {
   561→    console.error('[SPLICE] XML processing error:', err);
   562→    res.status(500).json({ error: err.message });
   563→  }
   564→});
