    50→        // Continue without queue - some endpoints will return 503
    51→      }
    52→
    53→      musicServicesLoaded = true;
    54→    } catch (err) {
    55→      console.warn('[SPLICE] Music services not available:', err.message);
    56→    }
    57→  }
