      if (isCollapsed) {
        ui.optionsPanel.classList.remove('collapsed');
        ui.optionsPanel.style.display = 'block';
        if (icon) icon.textContent = '-';
      } else {
        ui.optionsPanel.classList.add('collapsed');
        ui.optionsPanel.style.display = 'none';
        if (icon) icon.textContent = '+';
      }
    });

