function initRepetitionUI() {
  const detectBtn = document.getElementById('detectRepetitionsBtn');
  const removeBtn = document.getElementById('removeRepetitionsBtn');

  if (detectBtn) {
    detectBtn.addEventListener('click', detectRepetitions);
